import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E8%B1%86%E8%B1%89%E9%B2%AE%E9%B1%BC%E6%B2%B9%E9%BA%A6%E8%8F%9C%E6%88%90%E5%93%81.DKe1NHZu.jpg",c=JSON.parse('{"title":"豆豉鲮鱼油麦菜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/豆豉鲮鱼油麦菜/豆豉鲮鱼油麦菜.md","filePath":"guide/meat_dish/豆豉鲮鱼油麦菜/豆豉鲮鱼油麦菜.md","lastUpdated":1747107234000}'),o={name:"guide/meat_dish/豆豉鲮鱼油麦菜/豆豉鲮鱼油麦菜.md"};function s(n,l,d,_,u,h){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="豆豉鲮鱼油麦菜的做法" tabindex="-1">豆豉鲮鱼油麦菜的做法 <a class="header-anchor" href="#豆豉鲮鱼油麦菜的做法" aria-label="Permalink to &quot;豆豉鲮鱼油麦菜的做法&quot;">​</a></h1><p><img src="'+r+'" alt="豆豉鲮鱼油麦菜"></p><p>豆豉鲮鱼油麦菜是一到十分常见的菜，材料简单，操作方便，鲮鱼咸香，非常下饭。</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>油麦菜</li><li>甘竹牌鲮鱼罐头</li><li>蒜</li><li>生抽</li><li>白糖</li><li>食用油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份（约 2 人份）：</p><ul><li>油麦菜 500g</li><li>鲮鱼罐头 1 罐（250g 上下）</li><li>蒜 4 瓣</li><li>生抽 20g</li><li>白糖 3g</li><li>食用油 15ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ol><li><p><strong>食材处理</strong>：</p><ul><li>油麦菜洗净后切段</li><li>鲮鱼罐头打开后，把鲮鱼主刺去除，切成小段后备用</li><li>大蒜切成末</li></ul></li><li><p><strong>炒制过程</strong>：</p><ul><li>锅热倒入食用油，油热后加入洗净的油麦菜，炒到萎蔫出水后盛出备用，倒掉炒制出的水分</li><li>重新热锅后，加入鲮鱼罐头中的油，放入蒜末煸香</li><li>蒜末煸香后加入鲮鱼罐头中的豆豉，翻拌后加入切碎的鲮鱼块，再次翻拌</li><li>翻拌均匀后加入油麦菜，加入生抽和糖调味</li><li>调味加入后简单翻拌即可出锅装盘</li></ul></li></ol><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li><strong>注意事项</strong>： <ol><li>鲮鱼罐头本身带味道，加入生抽后够味，无需下盐</li></ol></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=i(o,[["render",s]]);export{c as __pageData,m as default};
