import{_ as i,c as a,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E8%B4%B5%E5%B7%9E%E8%BE%A3%E5%AD%90%E9%B8%A1.Gb5GXDG2.jpg",u=JSON.parse('{"title":"贵州辣子鸡的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/贵州辣子鸡/贵州辣子鸡.md","filePath":"guide/meat_dish/贵州辣子鸡/贵州辣子鸡.md","lastUpdated":1747108270000}'),s={name:"guide/meat_dish/贵州辣子鸡/贵州辣子鸡.md"};function d(n,l,h,o,_,p){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="贵州辣子鸡的做法" tabindex="-1">贵州辣子鸡的做法 <a class="header-anchor" href="#贵州辣子鸡的做法" aria-label="Permalink to “贵州辣子鸡的做法”">​</a></h1><p><img src="'+t+'" alt="贵州辣子鸡"></p><p>贵州人对吃鸡的执恋</p><ul><li>过节日，吃鸡</li><li>过生日，吃鸡</li><li>生病，吃鸡</li><li>有客人，吃鸡</li><li>家人团聚，吃鸡</li><li>不知道吃什么，那就吃鸡</li></ul><p>贵州辣子鸡多种配菜，香辣可口，香糯软烂</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>农村玉米鸡</li><li>土豆</li><li>大蒜</li><li>香叶</li><li>姜</li><li>老抽</li><li>花椒 or 麻椒</li><li>糍粑辣椒（花溪党武的辣椒，遵义的子弹头，条子椒，大方的皱椒混合之后打碎的辣椒）</li><li>豆瓣酱</li><li>啤酒</li><li>蒜苗</li><li>酒糟</li><li>食用油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>鸡三到四个人的量是四斤，人多可以依次累加</li><li>啤酒半瓶</li><li>姜手指头大小两个</li><li>糍粑辣椒 500g，会是拳头大小两坨</li><li>蒜苗三根</li><li>香叶 2 片</li><li>大蒜两个</li><li>土豆两个</li><li>菜籽油两斤，开始炸鸡会用很多</li><li>老抽 20 ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>在锅中加入和锅一半高度的油，将切成长条的土豆先炸至表面金黄然后捞出备用，等油温上至烤手时候将切好的鸡块放入锅中炸，并放入切好的生姜片和花椒</li><li>刚开始炸鸡的时候，油是浑浊的，因为鸡块里面有水的原因，等到油炸至清澈，鸡块就炸好了，然后捞出备用</li><li>现在锅里面的油可以捞三分之一出来，现在用不到这么多的油</li><li>将锅中剩余的油加热，加入糍粑辣椒，豆瓣酱，生姜片，炒出红油状，将炸好的鸡块翻炒均匀</li><li>等到鸡块都上色，加入老抽，倒入啤酒，啤酒一定要盖过鸡块，加上香叶盖上盖，闷十分钟，期间间隔翻炒</li><li>然后加入土豆条，大蒜（不用切，一颗一颗的最好），然后再闷 20 分钟</li><li>最后加入酒糟翻炒均匀再加入切好的蒜苗，就可以出锅了</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>操作时，需要注意翻炒，因为糍粑辣椒会糊掉</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',16)]))}const m=i(s,[["render",d]]);export{u as __pageData,m as default};
