import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/1.l9C_CXjZ.jpeg",s="/assets/2.ysZXmtVH.jpeg",p=JSON.parse('{"title":"酱排骨的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/酱排骨/酱排骨.md","filePath":"guide/meat_dish/酱排骨/酱排骨.md","lastUpdated":1747107234000}'),o={name:"guide/meat_dish/酱排骨/酱排骨.md"};function d(n,l,_,h,u,c){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="酱排骨的做法" tabindex="-1">酱排骨的做法 <a class="header-anchor" href="#酱排骨的做法" aria-label="Permalink to &quot;酱排骨的做法&quot;">​</a></h1><p>酱排骨其色泽酱红，肉质酥烂，骨香浓郁，汁浓味鲜，咸中带甜。</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>排骨</li><li>五香粉</li><li>料酒</li><li>食用油</li><li>白砂糖</li><li>老抽</li><li>生抽</li><li>蚝油</li><li>小米椒</li><li>蒜</li><li>姜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>排骨 300 g</li><li>五香粉 20 g</li><li>料酒 10 ml</li><li>食用油 30 ml</li><li>白砂糖 15 g</li><li>老抽 10 ml</li><li>生抽 10 ml</li><li>蚝油 5 ml</li><li>小米椒 1 个</li><li>蒜 2 粒</li><li>姜 2 片</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>起锅烧热水，放入排骨、姜片、料酒，煮开后用勺子舀去白色油沫，2-3 分钟后出锅</li><li>冷水清洗排骨，清洗 2-3 遍</li><li>小火起锅，加入食用油，加入白砂糖 ，轻轻搅拌到糖水变成黄色</li><li>倒入排骨翻炒 30 S 后，加入生抽、蚝油、五香粉、蒜、小米椒后翻炒 30 S 后，加入清水没过排骨</li><li>大火煮 30 分钟，加入老抽上色，再煮 10 分钟</li><li>起锅摆盘</li></ul><p><img src="'+r+'" alt="示例菜成品"><img src="'+s+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>增加味道的可以考虑加入豆瓣酱、番茄酱等</li><li>大火煮时间可以根据每个人情况做调整</li><li>煮排骨期间，可以顺道放入 2-3 个 鸡蛋一起煮，做成简易版卤蛋</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const f=i(o,[["render",d]]);export{p as __pageData,f as default};
