import{_ as l,c as e,o as i,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E9%A6%99%E7%85%8E%E4%BA%94%E8%8A%B1%E8%82%89.CnTviyvW.jpg",u=JSON.parse('{"title":"香煎五花肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/香煎五花肉/香煎五花肉.md","filePath":"guide/meat_dish/香煎五花肉/香煎五花肉.md","lastUpdated":1747034379000}'),o={name:"guide/meat_dish/香煎五花肉/香煎五花肉.md"};function s(d,a,n,c,m,_){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="香煎五花肉的做法" tabindex="-1">香煎五花肉的做法 <a class="header-anchor" href="#香煎五花肉的做法" aria-label="Permalink to &quot;香煎五花肉的做法&quot;">​</a></h1><p><img src="'+r+'" alt="香煎五花肉"></p><p>香煎五花肉一道简单易上手的菜。五花肉肥而不腻，生菜叶脆爽健康。稍微有下厨经验的人半小时便可制作完毕。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>五花肉条（推荐长宽高为 20cm*6cm*5cm）</li><li>生菜</li><li>酱油，盐，味精，料酒，姜蒜，油，豆瓣酱</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1 个人吃。</p><p>每份：</p><ul><li>五花肉条（推荐长宽高为 20cm*6cm*5cm）</li><li>生菜一朵</li><li>食用油 5ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将五花肉条沿长边切片，每片厚 1mm-1.5mm，备用</li><li>将切好的五花肉放置碗中，依次加入 8g 酱油，1g 盐，1g 味精，10g 料酒，两片姜，两朵拍扁的大蒜腌制 10 分钟</li><li>将生菜叶直接用手扒下来，洗干净，备用</li><li>热锅，倒入 5ml 食用油。油轻微冒烟后下入五花肉。单面煎制焦黄色后翻面，另一边同理。</li><li>五花肉出锅后，装盘。</li><li>将豆瓣酱抹到菜叶上，卷着五花肉即可食用</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>操作时，小心被溅出的油烫到</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',15)]))}const p=l(o,[["render",s]]);export{u as __pageData,p as default};
