import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E9%A6%99%E8%8F%87%E6%BB%91%E9%B8%A1.BRYJUm68.jpg",c=JSON.parse('{"title":"香菇滑鸡的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/香菇滑鸡/香菇滑鸡.md","filePath":"guide/meat_dish/香菇滑鸡/香菇滑鸡.md","lastUpdated":1747034379000}'),o={name:"guide/meat_dish/香菇滑鸡/香菇滑鸡.md"};function s(n,l,d,m,u,h){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="香菇滑鸡的做法" tabindex="-1">香菇滑鸡的做法 <a class="header-anchor" href="#香菇滑鸡的做法" aria-label="Permalink to &quot;香菇滑鸡的做法&quot;">​</a></h1><p><img src="'+r+'" alt="香菇滑鸡"></p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>大鸡腿</li><li>干香菇</li><li>姜</li><li>葱</li><li>蒜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>大鸡腿 2 个</li><li>干香菇 5 粒</li><li>姜 2 片</li><li>葱 2 颗</li><li>蒜 2 瓣</li><li>温水(30-40 ℃) 150ml</li><li>料酒 15ml</li><li>生抽 30ml</li><li>盐 1.5g</li><li>老抽 15ml</li><li>糖 15ml</li><li>香油 5ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>温水泡发干香菇</li><li>姜切小块，葱切段，蒜对半切小粒</li><li>鸡腿去骨（不去骨也可，只是略影响程序员吃饭的效率而已），切成小块</li><li>泡发的香菇一分为四，香菇水留着备用</li><li>鸡腿肉焯水 1 分钟，去除血沫和杂质</li><li>鸡腿肉中加料酒 15ml、生抽 15ml、盐 1.5g、老抽 15ml，抓匀</li><li>油温 3 成，下入鸡腿肉煸炒，等鸡腿肉金黄后盛出备用</li><li>锅留底油，下入葱、姜、蒜炒香，香菇入锅，大火翻匀</li><li>等待 20 秒会有香菇香味从锅中飘出，此时下入煸炒过的鸡腿肉，下入香菇水（全部，<strong>本程序员认为的灵魂操作</strong>）、糖 15ml、生抽 30ml</li><li>转中火不盖盖，咕嘟 2 分钟收浓汤汁，淋入香油 5ml，撒上葱花后即可关火、装盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>鸡腿去骨操作较复杂，本人经验可供参考： <ul><li>鸡腿于案板摆正，<strong>骨头正对程序员</strong>，从头部划一刀至最底部，刀口要深，确保有碰到骨头</li><li>用手扒拉肉到两边</li><li>用刀尖隔开骨头上沾着的肉</li><li>鸡腿翻面，刀紧贴骨头插入，挑断骨头与肉的连接点</li><li>用刀尖切断最后的粘连即可</li></ul></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=i(o,[["render",s]]);export{c as __pageData,p as default};
