import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"麻辣香锅的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/麻辣香锅.md","filePath":"guide/meat_dish/麻辣香锅.md","lastUpdated":1747107234000}'),r={name:"guide/meat_dish/麻辣香锅.md"};function o(d,l,s,n,h,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="麻辣香锅的做法" tabindex="-1">麻辣香锅的做法 <a class="header-anchor" href="#麻辣香锅的做法" aria-label="Permalink to &quot;麻辣香锅的做法&quot;">​</a></h1><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>青菜（油菜、油麦菜、菠菜）</li><li>无骨肉（猪肉、牛肉、鸡肉、鱼丸、火腿肠）</li><li>干豆腐</li><li>北京麻辣方便面</li><li>干辣椒</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>此数值大约是 3 个人的量。</p><ul><li>青菜共需 455 克，其中油菜、油麦菜、菠菜的比例按自己喜好分配即可</li><li>无骨肉共需 430 克，其中猪肉、牛肉、鸡肉、鱼丸、火腿肠的比例按自己喜好分配即可</li><li>干豆腐 152 克</li><li>北京麻辣方便面 1 袋</li><li>干辣椒 5 克</li><li>麻辣香锅调料 110 克</li></ul><p>以上内容中，青菜、无骨肉、干豆腐、北京麻辣方便面的配比为推荐配比，如果有自己喜欢的食材，可以按喜好重新分配比例。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ol><li>烧开一锅水（水量能没过第 2-4 步中的食材即可）。</li><li>在开水中放入青菜，焯 5 分钟后盛出备用。</li><li>在开水中放入无骨肉，焯 4 分钟后盛出备用。</li><li>在开水中放入北京麻辣方便面，煮 2 分钟后盛出备用。</li><li>倒出开水，擦干锅具，放入 105 克食用油，大火加热 30 秒。</li><li>放入麻辣香锅调料，翻炒 20 秒。</li><li>放入干辣椒，翻炒 10 秒。</li><li>放入焯过的青菜，改中火，翻炒 3 分钟。</li><li>放入焯过的无骨肉，翻炒 3 分钟。</li><li>放入煮过的北京麻辣方便面，用筷子翻动 1 分钟。</li><li>关火，出锅。</li></ol><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>关于火候的说明：</p><ul><li>大火通常温度在 240 摄氏度，以电磁炉为例，输入功率大约 2 千瓦。</li><li>中火通常温度在 120 摄氏度，以电磁炉为例，输入功率大约 1.4 千瓦。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const p=i(r,[["render",o]]);export{c as __pageData,p as default};
