import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"黄瓜炒肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/黄瓜炒肉.md","filePath":"guide/meat_dish/黄瓜炒肉.md","lastUpdated":1747034379000}'),r={name:"guide/meat_dish/黄瓜炒肉.md"};function o(d,l,s,n,h,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="黄瓜炒肉的做法" tabindex="-1">黄瓜炒肉的做法 <a class="header-anchor" href="#黄瓜炒肉的做法" aria-label="Permalink to &quot;黄瓜炒肉的做法&quot;">​</a></h1><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>黄瓜</li><li>猪瘦肉</li><li>食用油</li><li>生抽</li><li>盐</li><li>蒜</li><li>小米辣</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1 个人食用</p><p>总量：</p><ul><li>黄瓜 = 100 克 * 份数</li><li>猪肉 = 50 克 * 份数</li><li>油量 = 50 克 * 份数</li><li>盐量 = 10 克 * 份数</li><li>酱油 = 5 克 * 份数</li><li>蒜瓣 = 2 瓣 * 份数</li><li>小米辣 = 1 根 * 份数</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将猪瘦肉切片，放入碗中，倒入食用油 10 克，生抽，搅拌均匀，腌制 10 分钟</li><li>将黄瓜切去 5 厘米的头尾，剩余部分斜着切成 0.5 厘米的薄片</li><li>将黄瓜倒入碗中，撒上盐 8 克，搅拌均匀，腌制 5 分钟</li><li>将蒜瓣去皮，压扁，切成蒜末备用</li><li>将小米辣去丁切分成均匀 0.5 厘米的段状</li><li>热锅，倒油 40 克，等油温到冒烟，放入蒜蓉小米辣翻炒 5 次</li><li>放入腌制好的猪瘦肉，翻炒至肉熟变色</li><li>放入黄瓜，加入盐 2 克，大火翻炒均匀半分钟，出锅 <ul><li>如果无法保证大火，可能会有点生。可以考虑增加到 50 秒。</li></ul></li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const p=i(r,[["render",o]]);export{c as __pageData,p as default};
