import{_ as e,c as l,o as i,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E9%BB%94%E5%BC%8F%E8%85%8A%E8%82%A0%E5%A8%83%E5%A8%83%E8%8F%9C.DgLjJVuB.jpg",p=JSON.parse('{"title":"黔式腊肠娃娃菜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/黔式腊肠娃娃菜/黔式腊肠娃娃菜.md","filePath":"guide/meat_dish/黔式腊肠娃娃菜/黔式腊肠娃娃菜.md","lastUpdated":1747107234000}'),o={name:"guide/meat_dish/黔式腊肠娃娃菜/黔式腊肠娃娃菜.md"};function s(d,a,_,n,h,u){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="黔式腊肠娃娃菜的做法" tabindex="-1">黔式腊肠娃娃菜的做法 <a class="header-anchor" href="#黔式腊肠娃娃菜的做法" aria-label="Permalink to &quot;黔式腊肠娃娃菜的做法&quot;">​</a></h1><p><img src="'+r+'" alt="黔式腊肠娃娃菜"></p><p>黔式腊肠娃娃菜不需要掌握火候，也无需调料，非常适合懒癌的菜。制作时间 15 分钟，口味近似于川菜、湘菜，却是西南菜系中鲜见的不辣菜式，咸鲜可口、南北皆宜。</p><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>黔式腊肠</li><li>娃娃菜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>按照 1 人的份量：</p><ul><li>黔式腊肠 200g</li><li>娃娃菜 300g</li><li>水 750ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>锅内放入 750ml 水，开火加热至沸腾</li><li>放入腊肠，计时 13 分钟</li><li>放入娃娃菜，计时 2 分钟</li><li>关火，夹出腊肠及娃娃菜</li><li>娃娃菜切段、腊肠切片，装盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>根据锅的大小，建议以水量没过食材为宜，宁多勿少</li><li>如果没有刀具，娃娃菜和腊肠不切也可以</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=e(o,[["render",s]]);export{p as __pageData,m as default};
