import{_ as e,c as i,o as l,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E6%87%92%E4%BA%BA%E8%9B%8B%E6%8C%9E.D5XWq5oY.png",p=JSON.parse('{"title":"懒人蛋挞的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/semi_finished/懒人蛋挞/懒人蛋挞.md","filePath":"guide/semi_finished/懒人蛋挞/懒人蛋挞.md","lastUpdated":1747034379000}'),s={name:"guide/semi_finished/懒人蛋挞/懒人蛋挞.md"};function o(n,a,d,h,_,u){return l(),i("div",null,a[0]||(a[0]=[t('<h1 id="懒人蛋挞的做法" tabindex="-1">懒人蛋挞的做法 <a class="header-anchor" href="#懒人蛋挞的做法" aria-label="Permalink to &quot;懒人蛋挞的做法&quot;">​</a></h1><p><img src="'+r+'" alt="蛋挞成品"></p><p>蛋挞是一道常见的可口甜品，通常而言制作蛋挞是需要调和蛋挞液和制作蛋挞皮的，这个过程比较复杂和耗时，但是网购半成品恰恰解决解决以上的难题，初学者只需大约 40 分就可以完成。从今往后只要家里有烤箱，就可以化身烘焙达人，帮家人烤蛋挞！</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>需要烤箱 1 个（有上下火功能的最佳，也可以没有）</li><li>隔热手套 1 双</li><li>网购蛋挞液 1 盒，蛋挞皮 1 盒（附近的大超市也可以，比如家乐福、沃尔玛等等）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>蛋挞皮 1 个</li><li>蛋挞液约 10ml，到达挞皮的 4/5 最佳</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>烤箱 200 度，预热 10 分钟</li><li>在烤盘上放上蛋挞皮，蛋挞皮中倒入蛋挞液约 10ml，具体分量需要看蛋挞皮大小，通常倒入 4/5 即可</li><li>将烤盘放入烤箱内，上下火 190 度，烤 10 - 20 分。如果想快速烤出蛋挞液上的焦褐斑点，需要上火更高一些，通常是 200 - 210 度</li><li>蛋挞液烤出焦褐斑点，蛋挞皮完全蓬松冒油即可</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>操作时，可以根据焦褐斑大小适当调整时间，如果需要</li><li>可以在蛋挞中加 10g 碎芝士，就是一道芝士蛋挞啦~</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=e(s,[["render",o]]);export{p as __pageData,m as default};
