import{_ as e,c as l,o as i,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E7%82%B8%E8%96%AF%E6%9D%A1.aNo0hbVk.jpg",p=JSON.parse('{"title":"炸薯条的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/semi_finished/炸薯条/炸薯条.md","filePath":"guide/semi_finished/炸薯条/炸薯条.md","lastUpdated":1747108270000}'),h={name:"guide/semi_finished/炸薯条/炸薯条.md"};function s(n,a,d,o,c,u){return i(),l("div",null,a[0]||(a[0]=[r('<h1 id="炸薯条的做法" tabindex="-1">炸薯条的做法 <a class="header-anchor" href="#炸薯条的做法" aria-label="Permalink to “炸薯条的做法”">​</a></h1><p><img src="'+t+'" alt="炸薯条"></p><p>薯条 🍟 是一种土豆 🥔\\马铃薯 🥔\\洋芋 🥔 切成条状之后再油炸而成的快餐食物（在有的国家可能不算快餐），非常适合。相较于油炸，空气炸锅可能会更加易于避免崩溃和实现异步非阻塞。相较于自己动手切土豆再洗去淀粉并喷上油，使用半成品薯条可能会显著减少热量摄入前的热量消耗，四舍五入就是会显著减少热量摄入<s>前的热量消耗</s>。</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>1 袋半成品薯条（推荐品牌麦肯）</li><li>1 个空气炸锅（喜欢脆的切忌小牌子）</li></ul><blockquote><p>注意，使用的烹饪工具不同炸薯条可能有不同的做法，这里仅介绍使用「空气炸锅」的做法。</p></blockquote><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><ul><li>作为主食，1 人 1 顿 400g（以半成品为准）</li><li>作为小食，1 人 1 顿 1/4 主食质量+-50g</li></ul><blockquote><p>使用上述条件，按需求量（包括但不限于日常食量、心情和饭前运动情况）计算材料用量。</p></blockquote><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><h3 id="开封薯条" tabindex="-1">开封薯条 <a class="header-anchor" href="#开封薯条" aria-label="Permalink to “开封薯条”">​</a></h3><ul><li>开封大分量半成品薯条注意开口要小，可以有效减少长久储藏下薯条表面结霜。</li></ul><h3 id="预热空气炸锅" tabindex="-1">预热空气炸锅 <a class="header-anchor" href="#预热空气炸锅" aria-label="Permalink to “预热空气炸锅”">​</a></h3><ul><li>插电，200℃ 预热 5 分钟。</li><li>预热的目的是为了确保放入食材的时候锅内温度已经处于烹饪所需温度。</li><li>注意，预热完再拿出薯条，不应等薯条软化后再炸制。</li></ul><h3 id="炸薯条" tabindex="-1">炸薯条 <a class="header-anchor" href="#炸薯条" aria-label="Permalink to “炸薯条”">​</a></h3><ul><li>取出薯条放入空气炸锅，200℃20 分钟。</li><li>取出薯条的时候注意半成品薯条已经有油，所以要异步去做客户端内刀斯林的话需要使用夹持工具。</li><li>5~10 分钟时可以拿出锅体晃动使薯条受热均匀也防止粘连。</li></ul><h3 id="脆化-可选项" tabindex="-1">脆化（可选项） <a class="header-anchor" href="#脆化-可选项" aria-label="Permalink to “脆化（可选项）”">​</a></h3><ul><li>10 分钟~15 分钟时，拿出锅体，往已经干了的薯条表面喷 1 层面积为薯条表面积 2/3 的油。</li></ul><h3 id="取出-装盘" tabindex="-1">取出&amp;装盘 <a class="header-anchor" href="#取出-装盘" aria-label="Permalink to “取出&amp;装盘”">​</a></h3><ul><li>喜欢脆薯条的，取出后拿着锅体跳舞让空气经过薯条表面后装盘；喜欢软薯条的直接装盘。配合蘸酱或浇上酱汁更佳。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>番茄酱、蛋黄酱、蜂蜜芥末酱、<a href="./../../condiment/蒜香酱油">蒜香酱油</a>……炸薯条的晋级之路在于对酱料及酱料组合的探究。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',24)]))}const m=e(h,[["render",s]]);export{p as __pageData,m as default};
