import{_ as i,c as a,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const _=JSON.parse('{"title":"牛油火锅底料的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/semi_finished/牛油火锅底料.md","filePath":"guide/semi_finished/牛油火锅底料.md","lastUpdated":1747118079000}'),o={name:"guide/semi_finished/牛油火锅底料.md"};function n(t,l,d,s,h,g){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="牛油火锅底料的做法" tabindex="-1">牛油火锅底料的做法 <a class="header-anchor" href="#牛油火锅底料的做法" aria-label="Permalink to “牛油火锅底料的做法”">​</a></h1><p>重庆火锅又称毛肚火锅或麻辣火锅，是中国传统饮食方式之一。</p><p>其起源于明末清初的重庆嘉陵江畔，该菜式也是朝天门等码头船工纤夫的粗放餐饮方式。</p><p>其主要原料是牛毛肚、猪黄喉、鸭肠、牛血旺等。</p><p>一般初学者只需要 1 小时即可完成。</p><p>预估烹饪难度：★★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><h3 id="原料" tabindex="-1">原料 <a class="header-anchor" href="#原料" aria-label="Permalink to “原料”">​</a></h3><ul><li>牛油 4500 g</li><li>（色拉油 或 菜籽油） 1000 ml</li><li>纯猪油 500 g</li><li>豆瓣（郫县） 1000 g</li><li>糍粑辣椒 3000 g</li><li>老姜（切片） 250 g</li><li>大葱（切段） 100 g</li><li>洋葱（切丝） 100 g</li><li>大蒜（切片） 200 g</li><li>豆鼓（剁碎）（永川） 10 g</li><li>豆母子 140 g</li><li>红花椒 150 g</li><li>老油 ? 颗粒香料 100 g : 整形香料 150 g</li><li>麦芽粉（肉香） 12.5 g</li><li>白酒(52%VOL) 150 ml</li><li>老油 ?? 干辣椒面 15 g</li></ul><h3 id="香料" tabindex="-1">香料 <a class="header-anchor" href="#香料" aria-label="Permalink to “香料”">​</a></h3><ul><li>山奈 5 g</li><li>八角 10 g</li><li>草果 10 g</li><li>肉桂 10 g</li><li>丁香 5 g</li><li>小茴香 10 g</li><li>白芷 10 g</li><li>荜拨 5 g</li><li>香草 10 g</li><li>香果 10 g</li><li>山楂 10 g</li><li>陈皮 5 g</li><li>香茅 10 g</li><li>砂仁 10 g</li><li>白蔻 10 g</li><li>香叶 10 g</li></ul><h4 id="颗粒香料" tabindex="-1">颗粒香料 <a class="header-anchor" href="#颗粒香料" aria-label="Permalink to “颗粒香料”">​</a></h4><p>以上配料放入粉碎机打碎至大约 4 mm 颗粒备用。</p><h4 id="整形香料" tabindex="-1">整形香料 <a class="header-anchor" href="#整形香料" aria-label="Permalink to “整形香料”">​</a></h4><p>以上配料洗净备用。</p><h3 id="工具" tabindex="-1">工具 <a class="header-anchor" href="#工具" aria-label="Permalink to “工具”">​</a></h3><ul><li>粉碎机</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><ul><li>每份原料可制作 7.5 kg 火锅底料/火锅老油</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>锅置旺火（大火）放入牛油烧至 八成热(240±10°C) 时放入 <code>老姜、大葱、洋葱、大蒜 (各100g)</code>，炸干（吸尽异味（牛油腥味））后捞出扔掉。</li><li>放入 <code>(色拉油 || 菜籽油)、纯猪油</code>，等待锅中油温下降到 五成热(150±10°C) 时放入 <code>糍粑辣椒</code> 持续翻炒 5-8 分钟。</li><li>放入 <code>豆瓣</code> 炒散，转用 <strong>中小火</strong> 慢炒至料渣略发白翻砂（发出沙沙声）。</li><li>油在外观呈现樱桃红时放入 <code>姜片(150g)、大蒜(100g)</code> 炒香，大约 15 秒。</li><li>放入 <code>豆鼓、豆母子</code> 炒香，放入 <code>红花椒、小茴香</code> 炒香。</li><li>（老油） 此刻放入 颗粒香料</li><li>放入 <code>麦芽粉</code> 炒散，放入 <code>白酒</code> 炒散。</li></ul><h3 id="作为底料" tabindex="-1">作为底料 <a class="header-anchor" href="#作为底料" aria-label="Permalink to “作为底料”">​</a></h3><ul><li>起锅装入容器中，静置于温度低的环境(10-20) 5 天后再使用效果最佳。</li></ul><h3 id="作为老油" tabindex="-1">作为老油 <a class="header-anchor" href="#作为老油" aria-label="Permalink to “作为老油”">​</a></h3><ul><li>起锅装入容器中放入 <code>干辣椒面</code> 搅匀置放 24 小时，等待制作 <strong>老油</strong>。</li><li>将底料倒入锅中，加入 3/5 的开水用大火烧开 （底料:2/5 开水:3/5）。</li><li>烧开后表面会出现泡沫，将泡沫撇净。</li><li>转用 <strong>中小火</strong> 慢熬出味（约 25-30 分钟），过滤去渣。</li><li>等待容器中 <strong>油水分离</strong> 后，将表面的 <strong>油</strong> 撇净（将油打出来） 装入另外的容器。</li><li>将上一步所 <strong>撇</strong> 出来的 <strong>油</strong> 重新倒入 <strong>净锅</strong> 中，直至 <strong>炼干</strong> 油中水分起锅装入容器即为 <strong>火锅老油</strong>。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li><strong>警告：操作时请擦干手上水滴！以免水进入油锅中发生爆炸！造成严重烧伤！</strong></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',28)]))}const m=i(o,[["render",n]]);export{_ as __pageData,m as default};
