import{_ as e,c as i,o as l,ah as r}from"./chunks/framework.HzbgFGQH.js";const s="/assets/%E9%B8%A1%E7%BF%85%E4%B8%AD_0.bWzoHkSG.jpg",t="/assets/%E9%B8%A1%E7%BF%85%E4%B8%AD_1.Vh2u4qF9.jpg",m=JSON.parse('{"title":"空气炸锅鸡翅中的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/semi_finished/空气炸锅鸡翅中/空气炸锅鸡翅中.md","filePath":"guide/semi_finished/空气炸锅鸡翅中/空气炸锅鸡翅中.md","lastUpdated":1747108270000}'),n={name:"guide/semi_finished/空气炸锅鸡翅中/空气炸锅鸡翅中.md"};function _(o,a,d,h,c,p){return l(),i("div",null,a[0]||(a[0]=[r('<h1 id="空气炸锅鸡翅中的做法" tabindex="-1">空气炸锅鸡翅中的做法 <a class="header-anchor" href="#空气炸锅鸡翅中的做法" aria-label="Permalink to “空气炸锅鸡翅中的做法”">​</a></h1><p><img src="'+s+'" alt="鸡翅中"><img src="'+t+'" alt="鸡翅中"></p><p>空气炸锅做鸡翅中方便，这样自带油脂的食物味道很好，比 KFC 的好吃，吃完不<strong>用洗碗洗锅</strong>。</p><ul><li>烹饪时长：40 分钟（准备 3 分钟+解冻 20 分钟+下锅 17 分钟）</li><li>实际操作时间：5 分钟</li></ul><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>必备：鸡翅中（推荐买泰森的奥尔良鸡翅中，JD 打折的话 30 多一袋 454g 大概 12 个。上鲜和圣农的也买过，没这个方便。泰森的是腌好的，如果有时间的话可以买没腌的自己腌。）</li><li>可选：罗勒碎（撒上去纯粹为了好看）</li><li>可选：云南单山蘸水（代替烧烤料）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><ul><li>鸡翅中 6 个（泰森奥尔良鸡翅中，其他品牌例如圣农嘟嘟翅可能会大一些，请自行根据食量斟酌）</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>鸡翅从冰箱拿出来，鸡翼面朝下放入锡纸烤盘，撒上罗勒碎，盖上保鲜膜自然解冻 20 分钟</li><li>撒上罗勒碎，空气炸锅 200°C，10 分钟</li><li>翻面，撒上罗勒碎，空气炸锅 200°C，7 分钟</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>推荐购买温控比较好的空气炸锅，不然可能会糊</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const f=e(n,[["render",_]]);export{m as __pageData,f as default};
