import{_ as i,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E6%B2%99%E5%85%89%E9%B1%BC%E8%B1%86%E8%85%90%E6%B1%A4.dALYRt_J.jpg",s="/assets/%E6%98%82%E5%88%BA%E9%B1%BC%E8%B1%86%E8%85%90%E6%B1%A401.d0LUd7Fm.jpg",o="/assets/%E6%98%82%E5%88%BA%E9%B1%BC%E8%B1%86%E8%85%90%E6%B1%A402.BSArqubT.jpg",E=JSON.parse('{"title":"昂刺鱼豆腐汤的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/soup/昂刺鱼豆腐汤/昂刺鱼豆腐汤.md","filePath":"guide/soup/昂刺鱼豆腐汤/昂刺鱼豆腐汤.md","lastUpdated":1747108385000}'),d={name:"guide/soup/昂刺鱼豆腐汤/昂刺鱼豆腐汤.md"};function n(_,l,p,c,h,m){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="昂刺鱼豆腐汤的做法" tabindex="-1">昂刺鱼豆腐汤的做法 <a class="header-anchor" href="#昂刺鱼豆腐汤的做法" aria-label="Permalink to “昂刺鱼豆腐汤的做法”">​</a></h1><ul><li>昂刺鱼/沙光鱼 豆腐汤 刺少 肉嫩 营养丰盛、适合任何年龄的小伙伴</li></ul><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>昂刺鱼或者沙光鱼</li><li>豆腐</li><li>香葱</li><li>姜</li><li>食用油</li><li>料酒</li><li>食用盐</li><li>胡椒粉</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>昂刺鱼或者沙光鱼 一条</li><li>豆腐 100 g</li><li>香葱 一根</li><li>姜 一块</li><li>胡椒粉 3-5 g</li><li>食用油 15 ml</li><li>食用盐 10-15 g</li><li>开水 1L</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>鱼处理好后洗净，（特别注意肚内的血丝、不洗干净会有腥味），放入大碗中，倒入料酒、10g 姜片、5g 盐，腌制 15 分钟</li><li>豆腐切块，放入凉水浸泡 5 分钟，捞出备用</li><li>煎鱼前，先用生姜片擦一下锅防止粘锅，倒入油（油量为 15ml * 鱼的条数 ），烧热后放入鱼煎 2~3 分钟，期间需要晃动一下鱼，防止粘底，且需要翻一次身</li><li>待鱼全部煎好之后，倒入开水、5ml 料酒、姜片，小火转至大火，盖上锅盖、大火煮 10 分钟（水要稍微多一些，后面会蒸发掉一些）</li><li>见汤变白后倒入准备好的豆腐，调中火再煮 5 分钟，加入 10g 盐、3g 胡椒粉调味，最后撒上葱花出锅</li></ul><p><img src="'+r+'" alt="示例菜成品"></p><p><img src="'+s+'" alt="示例菜成品"></p><p><img src="'+o+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',15)]))}const B=i(d,[["render",n]]);export{E as __pageData,B as default};
