import{_ as l,c as e,o as i,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E6%9C%B1%E9%9B%80%E6%B1%A4.stlVSSnJ.jpg",u=JSON.parse('{"title":"朱雀汤的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/soup/朱雀汤/朱雀汤.md","filePath":"guide/soup/朱雀汤/朱雀汤.md","lastUpdated":1747108385000}'),s={name:"guide/soup/朱雀汤/朱雀汤.md"};function n(o,a,d,h,_,c){return i(),e("div",null,a[0]||(a[0]=[r('<h1 id="朱雀汤的做法" tabindex="-1">朱雀汤的做法 <a class="header-anchor" href="#朱雀汤的做法" aria-label="Permalink to “朱雀汤的做法”">​</a></h1><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>鸡蛋</li><li>香油（芝麻油）</li><li>白糖</li><li>水</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>按照 1 人份的份量：</p><ul><li>一个鸡蛋</li><li>500ml 水</li><li>20 克白糖（根据个人口味调整）</li><li>2ml 香油</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>鸡蛋在碗中打散，再倒入香油。</li><li>水烧开后，在沸腾状态下快速倒入盛有鸡蛋的碗中。</li><li>放入白糖。</li></ul><p><img src="'+t+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>鸡蛋一定要打散，水一定要在沸腾冒泡状态倒入鸡蛋液中，否则汤可能会浑浊影响口感</li><li>此为豫东地区做法，其他地区有不放白糖放盐的，口味不同，根据个人口味调整</li><li>此汤乃去火神器！</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const m=l(s,[["render",n]]);export{u as __pageData,m as default};
