import{_ as a,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const _=JSON.parse('{"title":"番茄牛肉蛋花汤的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/soup/番茄牛肉蛋花汤.md","filePath":"guide/soup/番茄牛肉蛋花汤.md","lastUpdated":1747107234000}'),r={name:"guide/soup/番茄牛肉蛋花汤.md"};function o(h,l,u,d,n,s){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="番茄牛肉蛋花汤的做法" tabindex="-1">番茄牛肉蛋花汤的做法 <a class="header-anchor" href="#番茄牛肉蛋花汤的做法" aria-label="Permalink to &quot;番茄牛肉蛋花汤的做法&quot;">​</a></h1><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>牛肉</li><li>番茄</li><li>鸡蛋</li><li>葱、姜、蒜</li><li>盐</li><li>胡椒粉</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>牛肉用量为 150 g/人</li><li>番茄用量为 1 个/人</li><li>鸡蛋用量为 1 个/人</li><li>盐用量为 2 g/人</li><li>胡椒粉用量为 0.5 g/人</li></ul><p>使用上述条件，计算出计划使用的原材料比例。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><h3 id="配菜处理" tabindex="-1">配菜处理 <a class="header-anchor" href="#配菜处理" aria-label="Permalink to &quot;配菜处理&quot;">​</a></h3><ul><li>牛肉切成薄片</li><li>番茄切成小块</li><li>葱切成葱花</li><li>姜切成姜片</li><li>蒜剁成蒜泥</li></ul><h3 id="牛肉的预处理" tabindex="-1">牛肉的预处理 <a class="header-anchor" href="#牛肉的预处理" aria-label="Permalink to &quot;牛肉的预处理&quot;">​</a></h3><ul><li>牛肉放入碗中</li><li>加盐、胡椒粉腌制 15-20 分钟</li></ul><h3 id="煮汤" tabindex="-1">煮汤 <a class="header-anchor" href="#煮汤" aria-label="Permalink to &quot;煮汤&quot;">​</a></h3><ul><li>加水煮开</li><li>加入姜片和牛肉片，煮至牛肉变色</li><li>加入番茄块，煮至番茄变软</li><li>打散鸡蛋液，缓慢地倒入锅中，用筷子搅拌形成蛋花</li><li>加入盐和胡椒粉调味</li><li>最后加入葱花，即可出锅</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>可以根据口味加入一定的醋或糖来调整口感</li><li>如果喜欢辣味，可以加入一定的辣椒或辣椒酱来调味</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',17)]))}const p=a(r,[["render",o]]);export{_ as __pageData,p as default};
