import{_ as i,c as a,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E7%BE%8A%E8%82%89%E6%B1%A4.Z5EmKosO.jpg",m=JSON.parse('{"title":"羊肉汤的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/soup/羊肉汤/羊肉汤.md","filePath":"guide/soup/羊肉汤/羊肉汤.md","lastUpdated":1747108270000}'),s={name:"guide/soup/羊肉汤/羊肉汤.md"};function o(n,l,d,h,c,p){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="羊肉汤的做法" tabindex="-1">羊肉汤的做法 <a class="header-anchor" href="#羊肉汤的做法" aria-label="Permalink to “羊肉汤的做法”">​</a></h1><p><img src="'+t+'" alt="羊杂汤"></p><p>羊肉汤/羊肉汤简单易，有抵御寒冷、温润养胃、开胃健脾的功效，富含钙、铁、蛋白质等营养物质。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>羊肉或羊杂</li><li>食用油</li><li>料酒</li><li>大葱</li><li>白胡椒粉</li><li>食用盐</li><li>孜然粉（可选）</li><li>香菜（可选）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>羊肉 300g</li><li>食用油 10ml</li><li>料酒 20ml</li><li>大葱 50g</li><li>开水 1000ml</li><li>白胡椒粉 1g</li><li>食用盐 5g</li><li>孜然粉 1g</li><li>香菜 20g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>羊肉切成长 5cm 宽 0.5cm 的块</li><li>大葱切成小段</li><li>羊肉放入锅中，加入 1000ml 常温水，加入料酒、大葱</li><li>煮沸 2 分钟后，捞出羊肉，使用常温水洗净，沥干水分</li><li>热锅加入食用油，加入羊肉，翻炒 2 分钟至羊肉表面微黄</li><li>加入开水，开到大火档位</li><li>5 分钟后，加入白胡椒粉、盐，继续煮沸 5 分钟</li><li>出锅之后，加入香菜、孜然粉，搅拌均匀</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>加入开水时，注意要开大火，使油脂与汤汁更好的融合</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const u=i(s,[["render",o]]);export{m as __pageData,u as default};
