import{_ as a,c as i,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E8%8F%8C%E8%8F%87%E7%82%96%E4%B9%B3%E9%B8%BD.Bjw1LD66.jpg",u=JSON.parse('{"title":"菌菇炖乳鸽的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/soup/菌菇炖乳鸽/菌菇炖乳鸽.md","filePath":"guide/soup/菌菇炖乳鸽/菌菇炖乳鸽.md","lastUpdated":1747108385000}'),s={name:"guide/soup/菌菇炖乳鸽/菌菇炖乳鸽.md"};function p(o,l,n,d,h,_){return e(),i("div",null,l[0]||(l[0]=[r('<h1 id="菌菇炖乳鸽的做法" tabindex="-1">菌菇炖乳鸽的做法 <a class="header-anchor" href="#菌菇炖乳鸽的做法" aria-label="Permalink to “菌菇炖乳鸽的做法”">​</a></h1><ul><li>菌菇炖乳鸽 汤鲜、肉嫩、营养丰富</li></ul><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>乳鸽</li><li>菌菇</li><li>玉米</li><li>姜</li><li>料酒</li><li>食用盐</li><li>瓦罐或者高压锅</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>2 人份：</p><ul><li>乳鸽 300 g</li><li>菌菇 100 g</li><li>玉米 200 g</li><li>姜 30 g</li><li>料酒 15 ml</li><li>食用盐 10 g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li><p>冷水洗干净热心摊主处理好的乳鸽</p></li><li><p>冷水锅中放入洗干净的乳鸽，加入 15ml 料酒与姜，水煮开即可捞出乳鸽，要不然会丢失营养</p></li><li><p>把乳鸽放到高压缩或者瓦罐中、倒入的水要没过乳鸽，放入生姜 20 g，玉米 200 g、菌菇 100 g</p><p>注意：高压锅 30 分钟，瓦罐需在水烧开后转小火慢炖 40-60 分钟</p></li><li><p>时间到了，盛到碗中，加入 3~5g 盐 即可</p></li></ul><p><img src="'+t+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const m=a(s,[["render",p]]);export{u as __pageData,m as default};
