import{_ as a,c as i,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const p=JSON.parse('{"title":"西红柿鸡蛋汤的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/soup/西红柿鸡蛋汤.md","filePath":"guide/soup/西红柿鸡蛋汤.md","lastUpdated":1747118079000}'),t={name:"guide/soup/西红柿鸡蛋汤.md"};function s(o,l,d,n,h,_){return e(),i("div",null,l[0]||(l[0]=[r('<h1 id="西红柿鸡蛋汤的做法" tabindex="-1">西红柿鸡蛋汤的做法 <a class="header-anchor" href="#西红柿鸡蛋汤的做法" aria-label="Permalink to “西红柿鸡蛋汤的做法”">​</a></h1><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>西红柿</li><li>鸡蛋</li><li>香油</li><li>味素</li><li>盐</li><li>葱、姜、蒜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><ul><li>西红柿 1 个</li><li>鸡蛋 1-2 个（依照自己的口味而定，喜欢吃鸡蛋就放 2 个，一般就放 1 个）</li><li>香油 2 滴</li><li>味素 5 克（可选）</li><li>盐 15 克</li><li>葱、姜、蒜共 15 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ol><li>将西红柿洗净，切块。</li><li>葱姜蒜切碎。</li><li>鸡蛋打到碗中，用筷子（或打蛋器）搅拌均匀。</li><li>热锅，并放入 15 毫升的油，待能从油中看到冒出一丝烟时，放入葱姜蒜翻炒 30 秒。</li><li>放入西红柿翻炒 1 分钟。</li><li>倒入水，水的高度大约为锅内菜品高度的 1.2 倍，并放入盐。</li><li>待开锅后，将鸡蛋液放入，并用筷子将鸡蛋打散，放入味素和香油。</li><li>等待 30 秒，关火出锅。</li></ol><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>味素可加可不加。</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',11)]))}const u=a(t,[["render",s]]);export{p as __pageData,u as default};
