import{_ as e,c as l,o as i,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"金针菇汤的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/soup/金针菇汤.md","filePath":"guide/soup/金针菇汤.md","lastUpdated":1747107234000}'),r={name:"guide/soup/金针菇汤.md"};function o(s,a,n,d,u,h){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="金针菇汤的做法" tabindex="-1">金针菇汤的做法 <a class="header-anchor" href="#金针菇汤的做法" aria-label="Permalink to &quot;金针菇汤的做法&quot;">​</a></h1><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>金针菇</li><li>鸡蛋（如需要）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>金针菇 400-500 克 （市场里面售卖的一袋即可）</li><li>食盐 15 克</li><li>味精 5 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ol><li>金针菇徒手掰散，越散越好<strong>不然容易藏牙</strong>，洗净备用。</li><li>用菜刀或者水果刀将上述金针菇段段切，可依据个人喜好，但长度不宜超过 5 cm。</li><li>将金针菇放入锅中，加水没过约食材总高度 1.1 倍，沸腾后<strong>等待 3 分钟</strong>。</li><li>加入味精和食盐并搅拌。</li><li>继续加热约 30 秒，关火装盘。</li></ol><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>如果喜欢在汤里加入鸡蛋，可在步骤 3 中水沸腾之后将打散的鸡蛋液倒入锅中。</li><li>出锅后可加入 3-4 滴香油（或者麻油）增香。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const p=e(r,[["render",o]]);export{c as __pageData,p as default};
