import{_ as a,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E9%93%B6%E8%80%B3%E8%8E%B2%E5%AD%90%E7%B2%A5.BM8ypJTK.png",c=JSON.parse('{"title":"银耳莲子粥的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/soup/银耳莲子粥/银耳莲子粥.md","filePath":"guide/soup/银耳莲子粥/银耳莲子粥.md","lastUpdated":1747107234000}'),o={name:"guide/soup/银耳莲子粥/银耳莲子粥.md"};function s(n,l,u,d,p,_){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="银耳莲子粥的做法" tabindex="-1">银耳莲子粥的做法 <a class="header-anchor" href="#银耳莲子粥的做法" aria-label="Permalink to &quot;银耳莲子粥的做法&quot;">​</a></h1><p><img src="'+r+'" alt="银耳莲子粥"></p><p>银耳莲子粥是一道营养非常丰富的粥。口味偏甜，具有养心安神的功效。</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>银耳</li><li>去心莲子</li><li>红枣</li><li>枸杞（可选）</li><li>冰糖</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>按照 1 人的份量：</p><ul><li>银耳 60g</li><li>去心莲子 20g</li><li>红枣 6g</li><li>枸杞 5-6g</li><li>冰糖 10-20g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>把银耳、莲子用清水浸泡 2 个小时，红枣浸泡 10 - 20 分钟，枸杞洗净，备用</li><li>在锅中倒入 600ml 水，烧开后依次放入银耳、莲子、红枣</li><li>等待水再次烧开后，盖上锅盖，转至中火继续熬</li><li>熬到大约 1 小时后，放入 5g - 10g 冰糖和 5g - 6g 枸杞，转至小火熬</li><li>小火继续熬 30 分钟，此时银耳开始呈现粘稠状态</li><li>再次放入 5g - 10g 冰糖，用勺子搅拌 5 - 10 分钟</li><li>关火，用勺子盛出</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>当银耳呈现粘稠状态时，需要用勺子及时搅拌，防止银耳糊在锅底</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const g=a(o,[["render",s]]);export{c as __pageData,g as default};
