import{_ as i,c as e,o as a,ag as o}from"./chunks/framework.DPDPlp3K.js";const t="/assets/%E5%BE%AE%E6%B3%A2%E7%82%89%E8%85%8A%E8%82%A0%E7%85%B2%E4%BB%94%E9%A5%AD.C7XQe3zT.png",h=JSON.parse('{"title":"微波炉腊肠煲仔饭的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/微波炉腊肠煲仔饭/微波炉腊肠煲仔饭.md","filePath":"guide/staple/微波炉腊肠煲仔饭/微波炉腊肠煲仔饭.md","lastUpdated":1747107234000}'),d={name:"guide/staple/微波炉腊肠煲仔饭/微波炉腊肠煲仔饭.md"};function r(c,l,s,n,u,_){return a(),e("div",null,l[0]||(l[0]=[o('<h1 id="微波炉腊肠煲仔饭的做法" tabindex="-1">微波炉腊肠煲仔饭的做法 <a class="header-anchor" href="#微波炉腊肠煲仔饭的做法" aria-label="Permalink to &quot;微波炉腊肠煲仔饭的做法&quot;">​</a></h1><p><img src="'+t+'" alt="微波炉腊肠煲仔饭"></p><p>程序员以单身汉居多 🐶，做再多的菜也会有一个人吃不完的烦恼，因此一份简单的腊肠煲仔饭则刚刚好。</p><p>使用微波炉烹制仅需 <code>15 分钟</code> ，既营养又美味，这是一道简单且细腻的主食，给 TA 露上一手吧。</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>工具 <ul><li>微波炉</li><li>2 个大碗（推荐微波炉专用碗）</li><li>1 个小碗</li></ul></li><li>原料 <ul><li>米 200 ml</li><li>腊肠 1 根</li><li>鸡蛋 1 个</li><li>红萝卜 1 个</li><li>盐</li><li>油 15 ml</li><li>生抽 10 ml</li><li>香葱 1 颗</li></ul></li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>1 人份。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将米淘洗干净后倒入 <code>饭碗</code> 内，加入 400ml 的水，<strong>盖上盖</strong></li><li>放入微波炉，高火，<code>6</code> 分钟，煮饭途中准备原料 <ul><li>切好腊肠</li><li>洗好青菜</li><li>切好红萝卜片</li><li>切好葱花</li><li><code>青菜碗</code> 中放入青菜、红萝卜片，倒入 10 ml 油，放入 5 g 盐</li><li><code>小碗</code> 中倒入 10 ml 生抽、5 ml 油</li></ul></li><li>6 分钟后，用毛巾或隔热手套取出碗，可以看见米饭已经八分熟</li><li>在米饭上摆入切片的腊肠，继续高火 <code>2</code> 分钟</li><li>取出腊肠饭，放入 <code>青菜碗</code>，高火 <code>4-5</code> 分钟</li><li>在腊肠饭上摆好青菜，磕入鸡蛋，看个人喜好继续高火 <code>40-60</code> 秒</li><li>取出腊肠饭，此时已经基本完成。</li><li>将 <code>小碗</code> 放入，继续高火 <code>30</code> 秒</li><li>在腊肠饭上淋上叮热的生抽，撒上葱花即可</li><li>多余的青菜可以沾着酱油吃</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const m=i(d,[["render",r]]);export{h as __pageData,m as default};
