import{_ as i,c as l,o as e,ah as s}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E6%88%90%E5%93%81.BIYbmMFm.jpg",g=JSON.parse('{"title":"日式咖喱饭的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/日式咖喱饭/日式咖喱饭.md","filePath":"guide/staple/日式咖喱饭/日式咖喱饭.md","lastUpdated":1747108270000}'),n={name:"guide/staple/日式咖喱饭/日式咖喱饭.md"};function t(h,a,d,o,p,c){return e(),l("div",null,a[0]||(a[0]=[s(`<h1 id="日式咖喱饭的做法" tabindex="-1">日式咖喱饭的做法 <a class="header-anchor" href="#日式咖喱饭的做法" aria-label="Permalink to “日式咖喱饭的做法”">​</a></h1><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><h3 id="主食材" tabindex="-1">主食材 <a class="header-anchor" href="#主食材" aria-label="Permalink to “主食材”">​</a></h3><ul><li>咖喱块（推荐品牌好侍）</li><li>土豆</li><li>胡萝卜</li><li>洋葱</li><li>肉（猪肉、鸡肉、牛肉均可）</li><li>蒜头</li></ul><h3 id="副食材" tabindex="-1">副食材 <a class="header-anchor" href="#副食材" aria-label="Permalink to “副食材”">​</a></h3><p>额外用作点缀的食材，可选</p><ul><li>花椰菜（清水煮开）</li><li>培根（即食）</li><li>煎蛋或<a href="./../../breakfast/太阳蛋">太阳蛋</a></li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>食材用量与咖喱成正比，计算部分以 <strong>半盒好侍咖喱块(115g)</strong> 为例。半盒约六碗份，做好的咖喱在冰箱冷藏后风味更佳，不用担心一个人吃不完。</p><ul><li>洋葱 2 个</li><li>土豆 2 个</li><li>胡萝卜 1 根</li><li>蒜头 2~3 瓣</li><li>肉 2 斤</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><h3 id="_1-食材准备" tabindex="-1">1. 食材准备 <a class="header-anchor" href="#_1-食材准备" aria-label="Permalink to “1. 食材准备”">​</a></h3><ul><li>胡萝卜去头尾，去皮，滚刀切</li><li>洋葱剥去外层去芯，切成月牙状</li><li>土豆去皮、切大块</li><li>肉切块状</li><li>剥蒜拍平切碎</li><li>咖喱块切碎，增加接触面积加速溶解</li></ul><h3 id="_2-烧煮过程" tabindex="-1">2. 烧煮过程 <a class="header-anchor" href="#_2-烧煮过程" aria-label="Permalink to “2. 烧煮过程”">​</a></h3><ul><li>热油锅放入蒜和肉，<strong>快速翻炒</strong>至肉<em>表面变白</em></li><li>加入胡萝卜，<strong>快速翻炒</strong>至均匀受热</li><li>加入洋葱，<strong>快速翻炒</strong>至洋葱<em>变透明状</em></li><li>加入土豆，保持翻炒至土豆<em>变软</em>（可以用筷子确认）</li><li>加水没过所有食材，沸腾后<strong>等待 15 分钟</strong></li><li>关火，加咖喱并搅拌</li><li>等待咖喱融化后再开火，缓慢<strong>搅拌 10 分钟</strong>，防止糊锅</li><li>在外观<em>呈粘稠状态</em>关火结束制作</li></ul><h3 id="_3-冷藏后加热" tabindex="-1">3.冷藏后加热 <a class="header-anchor" href="#_3-冷藏后加热" aria-label="Permalink to “3.冷藏后加热”">​</a></h3><p>冷藏的咖喱每次取出需要吃的份量，加热后盖在<a href="./../米饭/电饭煲蒸米饭">米饭</a>上。</p><ul><li>微波炉：单人份高火 2-3 分钟</li><li>锅：需额外加 50ml 水，加热时保持搅拌</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><h3 id="备注说明" tabindex="-1">备注说明 <a class="header-anchor" href="#备注说明" aria-label="Permalink to “备注说明”">​</a></h3><ul><li>步骤 1-6 可以在 2-5 的等待过程进行，在这个过程也可以用清水锅煮些蔬菜，或做个煎蛋。</li><li>2-5 到 2-6 之间，需要注意观察沸腾的水位线，如发现低于 2/3 的食材应加热水至没过食材。</li></ul><h3 id="流程图解" tabindex="-1">流程图解 <a class="header-anchor" href="#流程图解" aria-label="Permalink to “流程图解”">​</a></h3><div class="language-mermaid"><button title="复制代码" class="copy"></button><span class="lang">mermaid</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">graph TD</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">    A[处理肉, 胡萝卜, 洋葱, 土豆] --&gt; B(开油锅, 先后下锅炒熟)</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">    B --&gt; C[加水没过食材, 煮15分钟]</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">    C --&gt; D[咖喱切碎, 开水煮花椰菜等]</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">    C --&gt; E[加入咖喱搅拌 10 分钟]</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">    D --&gt; E</span></span></code></pre></div><h3 id="成品" tabindex="-1">成品 <a class="header-anchor" href="#成品" aria-label="Permalink to “成品”">​</a></h3><p><img src="`+r+'" alt="日式咖喱饭成品"></p><h3 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to “参考资料”">​</a></h3><ul><li><a href="http://t.cn/EJ77yFy" target="_blank" rel="noreferrer">世界美食教程的微博视频</a></li></ul><hr><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',30)]))}const m=i(n,[["render",t]]);export{g as __pageData,m as default};
