import{_ as i,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E9%A5%AD%E5%9B%A2.COPBLRnz.png",m=JSON.parse('{"title":"火腿饭团的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/火腿饭团/火腿饭团.md","filePath":"guide/staple/火腿饭团/火腿饭团.md","lastUpdated":1747108385000}'),s={name:"guide/staple/火腿饭团/火腿饭团.md"};function n(d,l,o,h,p,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="火腿饭团的做法" tabindex="-1">火腿饭团的做法 <a class="header-anchor" href="#火腿饭团的做法" aria-label="Permalink to “火腿饭团的做法”">​</a></h1><p><img src="'+r+'" alt="火腿饭团"> 好吃！富含碳水和蛋白质还有维生素。有手就行的制作难度，预计制作时间 1 h 。</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>火腿</li><li>米饭</li><li>水</li><li>冷冻青豆（可选）</li><li>冷冻玉米粒（可选）</li><li>海苔碎（可选）</li><li>喜欢的沙拉酱（推荐日式 mayo！）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 2 个人吃。</p><p>每份：</p><ul><li>火腿(100g)</li><li>米饭(125g)</li><li>水(90ml)</li><li>冷冻青豆(30g)</li><li>冷冻玉米粒(30g)</li><li>海苔碎(10g)</li><li>喜欢的沙拉酱(20g)</li><li>食用油 10-15ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>将米饭和水放到电饭锅里，点击米饭模式，等待完成</li><li>冷冻玉米粒和青豆放到锅里，加水没过所有食材，沸腾后静待 2 分钟后，捞出。</li><li>火腿切成 1cm 的方块</li><li>与此同时，加入 10ml 食用油，加入火腿翻炒至火腿上色</li><li>将米饭，火腿，海苔碎，青豆，玉米粒，沙拉酱放入碗中，混合均匀即可</li><li>装盘（如果有的话）</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>沙拉酱的程度可以根据个人口味酌情删减。</li><li>可以尝试混合沙拉酱。</li><li>参考资料：小红书 - 干饭选手又困了</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const u=i(s,[["render",n]]);export{m as __pageData,u as default};
