import{_ as a,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const p=JSON.parse('{"title":"炒方便面的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/炒方便面.md","filePath":"guide/staple/炒方便面.md","lastUpdated":1747107234000}'),r={name:"guide/staple/炒方便面.md"};function o(h,l,s,u,d,n){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="炒方便面的做法" tabindex="-1">炒方便面的做法 <a class="header-anchor" href="#炒方便面的做法" aria-label="Permalink to &quot;炒方便面的做法&quot;">​</a></h1><p>这是在探究了传统煮方便面的改良方向之后，进行的一次最成功的尝试。它能够让方便面的美味程度提升很大程度，简单好做。开始炒吧！</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>方便面</li><li>盐</li><li>鸡蛋</li><li>火腿肠（可选）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1 个人食用</p><p>总量：</p><ul><li>方便面用量为 1.2 包/人 向下取整。</li><li>鸡蛋的用量为 1.4 个/人 向下取整。</li><li>盐的用量为 鸡蛋的用量 * 2g。</li><li>火腿肠的用量为 0.7 个/人 向上取整。</li><li>食用油的用量为 10 - 18 ml / 人。</li></ul><p>使用上述条件，计算出计划使用的原材料比例。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><h3 id="火腿肠预处理" tabindex="-1">火腿肠预处理 <a class="header-anchor" href="#火腿肠预处理" aria-label="Permalink to &quot;火腿肠预处理&quot;">​</a></h3><ul><li>将火腿肠撕开包装，切成宽度 1cm 的小块。</li></ul><h3 id="面的处理" tabindex="-1">面的处理 <a class="header-anchor" href="#面的处理" aria-label="Permalink to &quot;面的处理&quot;">​</a></h3><ul><li>向煮锅中加入 300 ml 水。煮沸。</li><li>加入方便面面饼，煮 45 秒。煮的过程中将其挑动，把面条打散。</li><li>面条打散后立刻关火。</li><li>将面汤和面分离。用凉水冲一下面条。</li></ul><h3 id="酱料处理" tabindex="-1">酱料处理 <a class="header-anchor" href="#酱料处理" aria-label="Permalink to &quot;酱料处理&quot;">​</a></h3><ul><li>准备一个小碗，将方便面的调料包挤进去。 <ul><li>挤进去所有菜包</li><li>挤进去所有酱包</li><li>挤进去 50% - 80% 的粉包。（全部粉包都挤进去会很咸）</li></ul></li><li>将上一步的面汤取出 80ml，加入小碗，搅匀，得到调料碗。</li></ul><h3 id="鸡蛋的预处理" tabindex="-1">鸡蛋的预处理 <a class="header-anchor" href="#鸡蛋的预处理" aria-label="Permalink to &quot;鸡蛋的预处理&quot;">​</a></h3><ul><li>取出计算好的数量的鸡蛋，打入一个小碗。</li><li>每个鸡蛋加入 2g 盐。搅拌均匀。</li><li>热锅 20s，加入份数 * 8ml 油。</li><li>加入刚刚准备好的一碗鸡蛋。翻炒大约 20s 至鸡蛋形成固态即可。</li><li>将煎鸡蛋取出暂存。</li></ul><h3 id="最终步骤" tabindex="-1">最终步骤 <a class="header-anchor" href="#最终步骤" aria-label="Permalink to &quot;最终步骤&quot;">​</a></h3><ul><li>热锅 20s，增加锅内的油到份数 * 10ml。</li><li>加入第一步处理的火腿肠。翻炒 10 秒。</li><li>加入第二步的面。翻炒 30 秒。</li><li>加入第三步的调料碗。翻炒 30 秒。</li><li>加入第四步的煎鸡蛋。翻炒 30 秒。</li><li>关火盛盘即可。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>在北京，可以考虑在盛盘后加入芝麻酱。如果芝麻酱太浓稠，可以 1:1 兑水稀释。</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',24)]))}const m=a(r,[["render",o]]);export{p as __pageData,m as default};
