import{_ as a,c as i,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const _=JSON.parse('{"title":"炒河粉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/炒河粉.md","filePath":"guide/staple/炒河粉.md","lastUpdated":1747108270000}'),t={name:"guide/staple/炒河粉.md"};function h(d,l,n,s,o,c){return e(),i("div",null,l[0]||(l[0]=[r('<h1 id="炒河粉的做法" tabindex="-1">炒河粉的做法 <a class="header-anchor" href="#炒河粉的做法" aria-label="Permalink to “炒河粉的做法”">​</a></h1><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>炒河粉、猪肉/牛肉</li><li>炒料：盐、味精、老抽、生抽、孜然粉（或直接用河粉料）</li><li>其他调味料：胡椒粉</li><li>黄瓜、面筋块、绿豆芽、鸡蛋、蒜瓣、小葱、淀粉</li><li>盆、盘子</li></ul><blockquote><p>建议购买方案：在某宝、某买菜等平台上购买袋装鲜河粉，一般是1斤起售，冷藏保质期可达到两周，而且送河粉炒料！</p></blockquote><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><ul><li>河粉用量为 250 g/人，如果需要更大食量，可再加 100g/人 向下取整。</li><li>黄瓜丝 30g/人、面筋块 30g/人、绿豆芽 30g/人、打碎的鸡蛋 1 个/人。</li><li>拍碎的蒜瓣 2 个/人、小葱 1 根/人</li><li>河粉料可按 20g/人添加，若自行准备炒料可 10g 盐+2g 味精+3g 孜然粉。</li><li>淀粉可准备每 100g 肉+5g 淀粉比例准备。</li><li>老抽/生抽，分别为每 250g 河粉 10ml/15ml。</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><h3 id="准备工作" tabindex="-1">准备工作 <a class="header-anchor" href="#准备工作" aria-label="Permalink to “准备工作”">​</a></h3><ul><li>小葱切碎（葱白和葱叶分开）、蒜瓣拍碎，丢案板上备用。</li><li>打碎鸡蛋，捞一点蛋清到一只碗中，剩下的丢入另一只碗中备用。</li><li>将绿豆芽放入锅中，大火煮 60 秒。豆芽捞出，过凉水，放入盘中备用。</li><li>黄瓜切丝放入盘中备用，可和豆芽丢一起。</li><li>处理面筋，单独丢一个盘中。</li><li>肉切细条状，加入淀粉与刚刚碗中的鸡蛋清、胡椒粉，顺时针拌匀。</li><li>注：超市购买来的凉皮表面一般会有食用油，可以使用自来水清洗。面筋同样。</li><li>注：清洗面筋之后，请用手将面筋中的大量水分挤出（不需过于用力）。</li></ul><h3 id="热锅炒肉" tabindex="-1">热锅炒肉 <a class="header-anchor" href="#热锅炒肉" aria-label="Permalink to “热锅炒肉”">​</a></h3><ul><li>加入食用油，锅热倒出。</li><li>倒入处理好的肉，翻炒均匀至变色，倒入碗中备用。</li></ul><h3 id="炒制河粉" tabindex="-1">炒制河粉 <a class="header-anchor" href="#炒制河粉" aria-label="Permalink to “炒制河粉”">​</a></h3><ul><li>趁锅热，加入 20g 食用油（高血压人群可降低用量），倒入葱白、蒜爆炒出香。</li><li>加入河粉，淋入老抽提色，翻炒均匀后再加入河粉炒料，继续翻炒。</li><li>河粉即将透明时，放入炒制好的肉丝与面筋，并加入生抽提鲜，简单翻炒两次。</li><li>加入豆芽与黄瓜丝，翻炒至河粉完全透明。</li><li>关火！</li></ul><h3 id="最终步骤" tabindex="-1">最终步骤 <a class="header-anchor" href="#最终步骤" aria-label="Permalink to “最终步骤”">​</a></h3><ul><li>撒入葱叶点缀，把锅端起。</li><li>倒入盘中，开始干饭。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>个人口味根据地区、天气、时间均有不同，调料的具体使用量请据个人情况而定，喜欢辣椒的可以自行添加。</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',19)]))}const p=a(t,[["render",h]]);export{_ as __pageData,p as default};
