import{_ as e,c as l,o as i,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E8%8A%9D%E9%BA%BB%E7%83%A7%E9%A5%BC.B2TFM9Kr.jpg",p=JSON.parse('{"title":"芝麻烧饼的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/烧饼/芝麻烧饼.md","filePath":"guide/staple/烧饼/芝麻烧饼.md","lastUpdated":1747034379000}'),o={name:"guide/staple/烧饼/芝麻烧饼.md"};function s(n,a,d,h,u,_){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="芝麻烧饼的做法" tabindex="-1">芝麻烧饼的做法 <a class="header-anchor" href="#芝麻烧饼的做法" aria-label="Permalink to &quot;芝麻烧饼的做法&quot;">​</a></h1><p><img src="'+r+'" alt="示例菜成品"> 芝麻烧饼，外酥里软，简单易做。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>面粉</li><li>酵母粉</li><li>白糖</li><li>十三香</li><li>食用油</li><li>温水( 40℃ )</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>菜谱食材为 7 张饼的量</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>面团：300 克面粉，3 克酵母粉，3 克白糖，180 克温水，20 克食用油，醒面 10 分钟</li><li>油酥：小碗放 30 克面粉，2 克盐，4 克十三香，20 克食用油，拌匀后，静置</li><li>做饼：面擀成长方形，抹上调好的油酥，从一头卷起，切成 7 个面剂子，对折，用虎口收拢即可，先沾水再沾白芝麻，擀成小圆饼</li><li>烙饼：将电饼铛预热，倒入凉油（锅底铺满油），将擀好的饼放入电饼铛中，将饼的上方也刷点油，涂抹均匀盖上盖子，选大饼档,听到叮的一声出锅即可</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>如果没有电饼铛，也可以用平底锅</li><li>芝麻如果没有也可不用，一样好吃</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const m=e(o,[["render",s]]);export{p as __pageData,m as default};
