import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"热干面的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/热干面.md","filePath":"guide/staple/热干面.md","lastUpdated":1747034379000}'),r={name:"guide/staple/热干面.md"};function o(s,l,d,n,u,h){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="热干面的做法" tabindex="-1">热干面的做法 <a class="header-anchor" href="#热干面的做法" aria-label="Permalink to &quot;热干面的做法&quot;">​</a></h1><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>热干面特有的碱水面</li><li>小葱</li><li>酸豆角</li><li>肉末</li><li>蒜水</li><li>肉汤汁</li><li>萝卜干</li><li>芝麻酱</li><li>辣椒油</li><li>胡椒粉</li><li>酱油</li><li>食盐</li><li>鸡精</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>热干面特有的碱水面 (250g)</li><li>小葱 (10g)</li><li>酸豆角 (20g)</li><li>肉末 (30g)</li><li>蒜水 (30ml)</li><li>肉汤汁 (30ml)</li><li>萝卜干 (50g)</li><li>芝麻酱 (40ml)</li><li>辣椒油 (0-10ml)</li><li>胡椒粉(0-10g)</li><li>酱油(5ml)</li><li>食盐(3g)</li><li>鸡精(0-3g)</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>水煮沸，并加入碱水面，焯烫 25 秒钟捞起</li><li>撒上食盐、鸡精和胡椒粉</li><li>芝麻酱用 90ml 水稀释，搅匀，然后加入</li><li>加入 5ml 酱油，加入 30ml 肉汤汁和蒜水</li><li>加入萝卜干，肉末，酸豆角，葱花</li><li>拌均匀后开吃</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>辣椒油看个人口味添加</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const m=i(r,[["render",o]]);export{c as __pageData,m as default};
