import{_ as a,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"煮泡面加蛋的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/煮泡面加蛋.md","filePath":"guide/staple/煮泡面加蛋.md","lastUpdated":1747034379000}'),r={name:"guide/staple/煮泡面加蛋.md"};function o(s,l,d,n,u,h){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="煮泡面加蛋的做法" tabindex="-1">煮泡面加蛋的做法 <a class="header-anchor" href="#煮泡面加蛋的做法" aria-label="Permalink to &quot;煮泡面加蛋的做法&quot;">​</a></h1><p>煮泡面加蛋是能满足于各种人群的生存基本需求的重要主食，其材料方便易得，做法简单易上手且制作周期极短。</p><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>泡面</li><li>鸡蛋</li><li>水</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>单人，能支撑一个成年人不饥饿状态约 3 至 4 小时。</li></ul><p>每份：</p><ul><li>泡面 1 包</li><li>水 550ml-1000ml，根据锅的情况。以能完整将泡面浸入其中为准。</li><li>鸡蛋 1 个</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>先将水加热至沸腾（火候不做严格要求，使用热水会更快）</li><li>将取出的面饼放入锅中</li><li>将泡面里附带的佐料放入锅中</li><li>取出筷子轻微拨动泡面，使佐料充分溶解，面饼充分浸泡受热</li><li>盖上锅盖等待约 1 分钟至锅内水再次沸腾</li><li>去壳鸡蛋，加入锅中</li><li>等待约 3 至 4 分钟，即可</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>本指南中的鸡蛋包括但不仅限于：生鸡蛋，熟鸡蛋，卤蛋等</li><li>原材料泡面可在楼下便利店购买</li><li>可根据个人需要加入佐料，和其他食材包括但不仅限于：火腿肠，生菜，小肉丝，辣条，鱼干，虾仁，鸡腿</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const p=a(r,[["render",o]]);export{c as __pageData,p as default};
