import{_ as l,c as e,o as i,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E7%A9%BA%E6%B0%94%E7%82%B8%E9%94%85%E7%85%A7%E7%83%A7%E9%B8%A1%E9%A5%AD.CFKqqa66.jpg",u=JSON.parse('{"title":"空气炸锅照烧鸡饭的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/空气炸锅照烧鸡饭/空气炸锅照烧鸡饭.md","filePath":"guide/staple/空气炸锅照烧鸡饭/空气炸锅照烧鸡饭.md","lastUpdated":1747107234000}'),o={name:"guide/staple/空气炸锅照烧鸡饭/空气炸锅照烧鸡饭.md"};function s(n,a,d,_,p,c){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="空气炸锅照烧鸡饭的做法" tabindex="-1">空气炸锅照烧鸡饭的做法 <a class="header-anchor" href="#空气炸锅照烧鸡饭的做法" aria-label="Permalink to &quot;空气炸锅照烧鸡饭的做法&quot;">​</a></h1><p><img src="'+r+'" alt="空气炸锅照烧鸡饭成品"></p><p>空气炸锅照烧鸡饭是一道简单易做的菜。是一道既便利又便宜的美食，而且在品尝美味的同时，新手也能完全掌握！</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>丽滋饼干(Ritz crackers)</li><li>酱油</li><li>糖（白沙糖）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1 个人吃。</p><p>每份：</p><ul><li>鸡肉 900g</li><li>酱油 100-125ml</li><li>糖 60-65g</li><li>白醋 30-35ml</li><li>丽滋饼干（咸味曲奇可替代） 16 个(48g)</li><li>鸡蛋 2 个</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将酱油、糖和醋混合在一起,搅匀料汁备用</li><li>另一个碗中加入鸡肉、鸡蛋、1/2 料汁和压碎的丽滋饼干。搅拌均匀</li><li>空气炸锅用箔纸碗铺底，加入肉饼混合物，将剩余的料汁均匀的倒在上面</li><li><strong>350°</strong> 炸<strong>40 分钟</strong>。最好在米饭上食用</li><li>在外观<em>呈金黄酥脆</em>后出锅，切块盛盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>操作时，需要注意温度以免烫伤。</li><li>参考资料：<a href="https://cookpad.com/" target="_blank" rel="noreferrer">库克美食教程</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',15)]))}const m=l(o,[["render",s]]);export{u as __pageData,m as default};
