import{_ as a,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"电饭煲蒸米饭的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/米饭/电饭煲蒸米饭.md","filePath":"guide/staple/米饭/电饭煲蒸米饭.md","lastUpdated":1747107234000}'),r={name:"guide/staple/米饭/电饭煲蒸米饭.md"};function o(s,l,u,d,n,h){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="电饭煲蒸米饭的做法" tabindex="-1">电饭煲蒸米饭的做法 <a class="header-anchor" href="#电饭煲蒸米饭的做法" aria-label="Permalink to &quot;电饭煲蒸米饭的做法&quot;">​</a></h1><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>电饭煲</li><li>江南米或北方大米</li><li>水</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>一般一个人可以食用 100ml-200ml 的米。</li></ul><p>指尖量水法 （用于精准确定米水量的方法）</p><ul><li>江南米，米和水放在电饭煲的容器内，食指触及米时，水量能刚好没过食指的第一个指节又第二个指节 1/4 处（即大约 2.6 厘米）。</li><li>北方大米，米和水放在电饭煲的容器内，食指触及米，水量能刚好没过食指的第一个指节（即大约 2 厘米处）。</li></ul><p>使用上述条件，计算出计划使用的原材料比例。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>清洗米</li><li>将米和水一起加入电饭煲中。</li><li>连接电饭煲电源，进入加热模式。等待大约 30 分钟。</li><li>待电饭煲自动进入保温模式后。</li><li>将米在电饭煲中闷 10-15 分钟。</li><li>盛出米。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>技术总结 <ul><li>不论什么容器，水的量都是可以使用“指尖量水法”煮出好米饭。</li><li>水的多少是能够严重影响一碗好米饭的口感。</li><li>南方一般食用江南米，北方一般食用北方大米，江南米会比较细长，而北方大米会比较短且圆。</li><li>米饭不同品种的米，不同的水量会极度影响口感。</li></ul></li><li>更多信息请参考使用的电饭煲的说明书。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const p=a(r,[["render",o]]);export{c as __pageData,p as default};
