import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E8%80%81%E5%8F%8B%E7%8C%AA%E8%82%89%E7%B2%89.C86axgOS.jpg",c=JSON.parse('{"title":"老友猪肉粉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/老友猪肉粉/老友猪肉粉.md","filePath":"guide/staple/老友猪肉粉/老友猪肉粉.md","lastUpdated":1747034379000}'),o={name:"guide/staple/老友猪肉粉/老友猪肉粉.md"};function s(n,l,d,m,u,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="老友猪肉粉的做法" tabindex="-1">老友猪肉粉的做法 <a class="header-anchor" href="#老友猪肉粉的做法" aria-label="Permalink to &quot;老友猪肉粉的做法&quot;">​</a></h1><p><img src="'+r+'" alt="示例菜成品"></p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>米粉</li><li>猪肉</li><li>酸笋</li><li>剁椒</li><li>豆豉</li><li>大蒜</li><li>料酒</li><li>生抽</li><li>白糖</li><li>米醋</li><li>盐</li><li>油</li><li>生粉</li><li>胡椒粉</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>米粉（250g 记得 50 度的温水泡半小时）</li><li>猪肉(50g)</li><li>酸笋(50g)</li><li>剁椒（15g 辣椒剁完后, 个人需求适当放。 ）</li><li>豆豉(30g)</li><li>大蒜(10g)</li><li>料酒(10-20ml)</li><li>生抽(15ml)</li><li>白糖（5g 如果不喜欢糖，可以考虑不放）</li><li>米醋(5ml)</li><li>盐(5ml)</li><li>油(15ml)</li><li>生粉(15ml)</li><li>胡椒粉(10ml)</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>全部猪肉用料酒、盐、生抽、生粉、胡椒粉倒在一个碗里调味,备用</li><li>热锅不放油,下全部酸笋把水份炒干，炒干的酸笋中间留点空间</li><li>放入 10ml - 15ml 食用油与全部大蒜、 剁椒、 豆豉到炒干的酸笋中间到炒干的酸笋中间,全部推到中间炒出香味</li><li>放入全部调味好的猪肉,持续放入 10ml 生抽炒一分钟</li><li>放入 5ml 米醋、 10ml 生抽、450ml 清水一起煮开</li><li>水煮开后,放入温水泡好的米粉,继续煮 3 分钟就可以盛盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>记得米粉必须是泡过的才好吃,没有泡过的话是很难吃的！</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=i(o,[["render",s]]);export{c as __pageData,p as default};
