import{_ as i,c as a,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const c=JSON.parse('{"title":"肉蛋盖饭的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/肉蛋盖饭.md","filePath":"guide/staple/肉蛋盖饭.md","lastUpdated":1747118079000}'),t={name:"guide/staple/肉蛋盖饭.md"};function s(d,l,n,h,o,p){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="肉蛋盖饭的做法" tabindex="-1">肉蛋盖饭的做法 <a class="header-anchor" href="#肉蛋盖饭的做法" aria-label="Permalink to “肉蛋盖饭的做法”">​</a></h1><p>肉蛋盖饭适合于单人简易晚餐，烹饪大约需要十五分钟。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>米饭</li><li>鸡蛋</li><li>肉馅</li><li>老抽</li><li>生抽</li><li>醋</li><li>红葱油（可选）</li><li>葱</li><li>油</li><li>糖</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>一份为一人餐，多人餐请等比例增加，但需要考虑锅的容量。</p><p>一份：</p><ul><li>米饭 240g</li><li>鸡蛋 4 个</li><li>肉馅 300g</li><li>老抽 10ml</li><li>生抽 25ml</li><li>醋 20ml</li><li>红葱油可选 10g</li><li>葱 10g</li><li>油 30ml</li><li>糖 15g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>煮好米饭，通常使用买米赠送的量杯，一杯米 240g</li><li>锅中放油 30ml</li><li>放入肉馅，调中火煎至两面微焦</li><li>将鸡蛋打入锅中，不要打散，盖上锅盖</li><li>调一个碗汁，碗中放入计算中的对应数量的老抽，生抽，醋，糖，红葱油，搅拌均匀</li><li>打开锅盖，将碗汁倒入锅中，等待三分钟</li><li>关火，将肉蛋盖到米饭上</li><li>安全检查，开始食用盖饭</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>如果喜欢甜口可以增加 10g 糖</li><li>请务必保证做完饭后关掉燃气设备以免发生危险</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=i(t,[["render",s]]);export{c as __pageData,m as default};
