import{_ as i,c as t,o as a,ag as s}from"./chunks/framework.DPDPlp3K.js";const e="/assets/%E8%8C%84%E5%AD%90%E8%82%89%E7%85%8E%E9%A5%BC.CRI3Igpn.jpg",r="/assets/1%E8%8C%84%E7%89%87%E8%82%89%E7%89%87.DgqjnOzk.jpg",o="/assets/2%E7%B1%B3%E7%B2%89250g.BjmnAlYW.jpg",p="/assets/3%E7%B1%B3%E7%B2%89%E9%9D%A2%E7%B2%89%E9%B8%A1%E8%9B%8B.T4ZBbU2T.jpg",n="/assets/4%E6%B7%B7%E5%90%88.CiDLWxqj.jpg",_="/assets/5%E8%B5%B7%E9%94%85%E7%83%A7%E6%B2%B9.D5FrxGy6.jpg",c="/assets/6%E5%BC%80%E5%A7%8B%E7%85%8E.DCG9CCzZ.jpg",g="/assets/7%E6%92%92%E7%9B%90%E5%87%86%E5%A4%87%E8%B5%B7%E9%94%85.iOtIDDiQ.jpg",b=JSON.parse('{"title":"茄子肉煎饼的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/茄子肉煎饼/茄子肉煎饼.md","filePath":"guide/staple/茄子肉煎饼/茄子肉煎饼.md","lastUpdated":1747034379000}'),u={name:"guide/staple/茄子肉煎饼/茄子肉煎饼.md"};function E(m,l,d,h,B,f){return a(),t("div",null,l[0]||(l[0]=[s('<h1 id="茄子肉煎饼的做法" tabindex="-1">茄子肉煎饼的做法 <a class="header-anchor" href="#茄子肉煎饼的做法" aria-label="Permalink to &quot;茄子肉煎饼的做法&quot;">​</a></h1><p><img src="'+e+'" alt="茄子肉煎饼成品"></p><p>茄子肉煎饼是一道简单易做的饼类主食。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>米粉（指用大米研磨成的粉）</li><li>小麦粉</li><li>鸡蛋</li><li>煮熟的腊肉</li><li>茄子（买长条状的，越圆越好）</li><li>食用油</li><li>食盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>制作 1 份，1 份正好够 2 个人吃。</p><p>每份：</p><ul><li>米粉 250g</li><li>面粉 50g</li><li>鸡蛋 1 个</li><li>煮熟的腊肉 100g</li><li>茄子 1 根（约 10-15cm 长）</li><li>食用油 10-15ml</li><li>食盐 1-2g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将茄子去皮后切成片，将腊肉切成片，备用</li></ul><p><img src="'+r+'" alt="茄片肉片"></p><ul><li>依次向盆中加入 250g 米粉（大米研磨成的粉）、50g 面粉和 1 个鸡蛋</li></ul><p><img src="'+o+'" alt="米粉250g"></p><p><img src="'+p+'" alt="米粉面粉鸡蛋"></p><ul><li>边用筷子搅拌，边加入清水（<strong>清水用于调节粘稠度</strong>），使米粉、面粉、鸡蛋混合成面糊,当面糊能够附着在茄片、肉片上而不掉落时停止加水，而后将所有茄片和肉片放入面糊中，用面糊充分包裹</li></ul><p><img src="'+n+'" alt="混合"></p><ul><li>平底锅加入食用油<strong>10-30ml</strong>，开小火</li></ul><p><img src="'+_+'" alt="起锅烧油"></p><ul><li>用筷子或勺子把裹了面糊的茄片、肉片放入锅中，先煎至两面金黄，再煎<strong>3-6分钟</strong>（<strong>煎的过程中，食用油会变少，可再添加食用油</strong>）</li></ul><p><img src="'+c+'" alt="开始煎"></p><ul><li>撒盐，翻炒均匀，起锅装盘</li></ul><p><img src="'+g+'" alt="撒盐准备起锅"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>用小火煎</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',27)]))}const j=i(u,[["render",E]]);export{b as __pageData,j as default};
