import{_ as l,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"醪糟小汤圆的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/醪糟小汤圆.md","filePath":"guide/staple/醪糟小汤圆.md","lastUpdated":1747107234000}'),r={name:"guide/staple/醪糟小汤圆.md"};function o(s,a,d,n,u,h){return e(),i("div",null,a[0]||(a[0]=[t('<h1 id="醪糟小汤圆的做法" tabindex="-1">醪糟小汤圆的做法 <a class="header-anchor" href="#醪糟小汤圆的做法" aria-label="Permalink to &quot;醪糟小汤圆的做法&quot;">​</a></h1><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>小汤圆</li><li>醪糟</li><li>白糖</li><li>枸杞（可选）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1-2 个人食用</p><p>总量：</p><ul><li>水 300 毫升 * 份数</li><li>小汤圆 250 克 * 份数</li><li>醪糟 50 克 * 份数</li><li>枸杞 5 颗 * 份数</li><li>白糖</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将水倒入锅中并煮沸</li><li>放入小汤圆煮 8 分钟</li><li>放入醪糟和枸杞再煮 2 分钟</li><li>盛入碗中根据个人口味加入白糖并搅拌均匀</li><li>吃</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>白糖的用量根据个人口味更改</li><li>若喜欢更浓的口感可以减少 100 毫升内的水</li><li>若喜欢软点的枸杞口感，可以将枸杞和小汤圆同时煮</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=l(r,[["render",o]]);export{c as __pageData,p as default};
