import{_ as e,c as l,o as i,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E7%B1%B3%E9%A5%AD.0Ne1iNry.jpg",u=JSON.parse('{"title":"鲣鱼海苔玉米饭的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/鲣鱼海苔玉米饭/鲣鱼海苔玉米饭.md","filePath":"guide/staple/鲣鱼海苔玉米饭/鲣鱼海苔玉米饭.md","lastUpdated":1747108385000}'),s={name:"guide/staple/鲣鱼海苔玉米饭/鲣鱼海苔玉米饭.md"};function n(o,a,d,_,h,c){return i(),l("div",null,a[0]||(a[0]=[r('<h1 id="鲣鱼海苔玉米饭的做法" tabindex="-1">鲣鱼海苔玉米饭的做法 <a class="header-anchor" href="#鲣鱼海苔玉米饭的做法" aria-label="Permalink to “鲣鱼海苔玉米饭的做法”">​</a></h1><p><img src="'+t+'" alt="示例菜成品"></p><p>空气炸锅羊排超级懒人版，味道尚可，主要看羊排的品质。</p><ul><li>烹饪总时长：40 分钟（准备 3 分钟+煮饭 40 分钟+拌饭 2 分钟）</li><li>实际操作时间：5 分钟</li></ul><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>必备：东北米（金龙鱼 30 元 5kg 的就行，推荐使用米家 IH 电饭煲煮，教程用的是米家小饭煲，<strong>不推荐使用高压锅煮饭</strong>）</li><li>必备：鲣鱼海苔碎（JD 和淘宝都有，可以搜索：日式拌饭料）</li><li>必备：玉米粒（淘宝搜索：玉米粒 即食）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><ul><li>鲣鱼海苔碎 20g</li><li>玉米粒 80g/袋</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>盛好米饭，放入玉米粒拌好</li><li>放入鲣鱼海苔碎</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>使用米家小饭煲煮东北米的话，水没过米 3 毫米就行，太多水煮出来不是一粒一粒的饭</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=e(s,[["render",n]]);export{u as __pageData,m as default};
