import{_ as a,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"麻油拌面的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/staple/麻油拌面.md","filePath":"guide/staple/麻油拌面.md","lastUpdated":1747034379000}'),r={name:"guide/staple/麻油拌面.md"};function o(s,l,d,n,u,h){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="麻油拌面的做法" tabindex="-1">麻油拌面的做法 <a class="header-anchor" href="#麻油拌面的做法" aria-label="Permalink to &quot;麻油拌面的做法&quot;">​</a></h1><p>省吃俭用懒人的菜：麻油拌面：想必大家都会有节约开销的时刻吧，附上个人耐吃又省钱的食谱。不需要太多的步骤简单的煮，捞，吃。</p><ul><li>单身的朋友懒惰出门，又不想花钱，简简单单就一餐。</li><li>非单身的朋友想存钱，让女友花钱，简简单单就一餐。</li></ul><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>风干快熟面/任何牌子的快熟面（不需要调味料）</li><li>麻油</li><li>胡椒粉</li><li>老抽</li><li>盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>水 1 升</li><li>快熟面 1 块</li><li>麻油 15ml</li><li>老抽 10 克</li><li>盐 30 克（可选，这 30g 盐不会被全部食用）</li><li>胡椒粉 10 克</li><li>生抽 5 克（可选）</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将水倒入锅中并煮沸 （喜欢吃 q 弹面的同学，可在水里加入 30 克盐，用盐水煮出来的面会比较 q 弹）</li><li>将快熟面放入锅中 3 分钟（也可参考当下品牌快熟面的烹饪时间）</li><li>当面开始散了可以开始搅拌，让面受热均匀</li><li>将水滤干把面倒入碗中</li><li>按照上面的计量放入麻油，老抽，胡椒粉，生抽（可选）</li><li>筷子搅拌均匀</li><li>一道简单即省钱的懒人麻油拌面就完成啦</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=a(r,[["render",o]]);export{c as __pageData,p as default};
