import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E4%B8%8A%E6%B1%A4%E5%A8%83%E5%A8%83%E8%8F%9C.Bn8FYE6N.png",c=JSON.parse('{"title":"上汤娃娃菜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/上汤娃娃菜/上汤娃娃菜.md","filePath":"guide/vegetable_dish/上汤娃娃菜/上汤娃娃菜.md","lastUpdated":1747034379000}'),p={name:"guide/vegetable_dish/上汤娃娃菜/上汤娃娃菜.md"};function o(s,l,d,n,_,h){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="上汤娃娃菜的做法" tabindex="-1">上汤娃娃菜的做法 <a class="header-anchor" href="#上汤娃娃菜的做法" aria-label="Permalink to &quot;上汤娃娃菜的做法&quot;">​</a></h1><p>上汤娃娃菜的做法 （素菜、减肥餐）</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>娃娃菜</li><li>皮蛋</li><li>午餐肉（火腿肠）</li><li>葱</li><li>姜</li><li>蒜</li><li>盐</li><li>糖</li><li>淀粉</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>注意，这道菜仅有足够 2-4 人食用的版本。</p><ul><li>娃娃菜 700g</li><li>金针菇 10g（看个人喜好, 不喜欢 see you tomorrow 的就不放 😂）</li><li>皮蛋 一个（没有也可以不放）</li><li>午餐肉（火腿肠都可以替代）</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li><p>娃娃菜洗净, 竖着切开切成段。</p></li><li><p>葱 3g 切 小段。蒜 10g 切片。姜 10g 切小片。</p></li><li><p>皮蛋切成丁, 火腿肠或者午餐肉切成丁（1cm 大小的丁）</p></li><li><p>金针菇洗净撕开</p></li><li><p>烧热水娃娃菜放进去十秒钟出一下水捞出。</p></li><li><p>热锅凉油, 加热锅倒入油过一遍就倒出来, 重新倒入一点油。</p></li><li><p>调至小火加入葱姜蒜，煎炒出香味即可。</p></li><li><p>加入适 300g 清水（水量没过娃娃菜即可）, 放入娃娃菜, 金针菇, 午餐肉</p></li><li><p>加入调味料蚝油、糖、盐、味精烧开。</p></li><li><p>煮 3 分钟, 煮开后开始装盘, 盛出娃娃菜后皮蛋放在上面把汤汁浇上去就可以了</p></li><li><p><img src="'+r+'" alt="上汤娃娃菜"></p><p>拍照技术有限, 味道还是很不错的</p></li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const m=i(p,[["render",o]]);export{c as __pageData,m as default};
