import{_ as i,c as a,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const m=JSON.parse('{"title":"凉拌油麦菜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/凉拌油麦菜.md","filePath":"guide/vegetable_dish/凉拌油麦菜.md","lastUpdated":1747108385000}'),t={name:"guide/vegetable_dish/凉拌油麦菜.md"};function s(d,l,n,h,o,_){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="凉拌油麦菜的做法" tabindex="-1">凉拌油麦菜的做法 <a class="header-anchor" href="#凉拌油麦菜的做法" aria-label="Permalink to “凉拌油麦菜的做法”">​</a></h1><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>油麦菜</li><li>芝麻酱</li><li>酱油</li><li>醋</li><li>蚝油</li><li>白糖</li><li>香油</li><li>蒜</li><li>盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1-2 个人食用</p><p>总量：</p><ul><li>1 颗 油麦菜（约 200g） * 份数</li><li>15ml 醋 * 份数</li><li>5ml 酱油 * 份数</li><li>10ml 芝麻酱 * 份数</li><li>5ml 香油 * 份数</li><li>5g 糖 * 份数</li><li>10ml 蚝油 * 份数</li><li>两<strong>头</strong>蒜 * 份数</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>蒜拍碎切末</li><li>醋，酱油，芝麻酱，香油，糖，蚝油，蒜末放到碗里搅拌均匀</li><li>油麦菜切段，每段不超过 4cm</li><li>油麦菜放到一个大点的盆里,倒入上述碗中酱料,充分搅拌均匀.</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>芝麻酱可以用花生酱代替</li><li>芝麻酱一定要和油麦菜混合均匀才更好吃.</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=i(t,[["render",s]]);export{m as __pageData,p as default};
