import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/1.DTpuWHCm.jpeg",p=JSON.parse('{"title":"凉拌莴笋的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/凉拌莴笋/凉拌莴笋.md","filePath":"guide/vegetable_dish/凉拌莴笋/凉拌莴笋.md","lastUpdated":1747034379000}'),o={name:"guide/vegetable_dish/凉拌莴笋/凉拌莴笋.md"};function s(d,l,n,h,u,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="凉拌莴笋的做法" tabindex="-1">凉拌莴笋的做法 <a class="header-anchor" href="#凉拌莴笋的做法" aria-label="Permalink to &quot;凉拌莴笋的做法&quot;">​</a></h1><p>凉拌莴笋，开胃小菜</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>莴笋</li><li>萝卜</li><li>小米辣</li><li>姜</li><li>蒜头</li><li>盐</li><li>食用油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>莴笋 1 根</li><li>萝卜 0.25 根</li><li>小米辣 2 个</li><li>姜 1 片</li><li>蒜头 2 粒</li><li>盐 5 g</li><li>食用油 25 ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>莴笋削皮，切小条。萝卜切条，一起放入大碗，加入盐搅拌，放置 10 分钟</li><li>放置后的莴笋用水清洗 1-2 遍</li><li>起锅烧水，放入莴笋，水煮 1 分钟 捞出，沥干水分，放入大碗</li><li>起锅烧油，放入姜片、蒜粒、小米椒煸炒 30-45 S ，倒入莴笋中</li><li>搅拌充分，端盘</li></ul><p><img src="'+r+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>萝卜非必须，只是为了增加菜色</li><li>端盘后，可以根据个人口味，额外增加生抽、白砂糖、香醋、芝麻等佐料</li><li>莴笋条的大小看个人口感决定，但莴笋皮要尽量多削点，不然真的影响口感</li><li>冰镇效果更加</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=i(o,[["render",s]]);export{p as __pageData,m as default};
