import{_ as i,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E5%B0%8F%E7%82%92%E8%97%95%E4%B8%81.DHwnfVQj.jpg",m=JSON.parse('{"title":"小炒藕丁的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/小炒藕丁/小炒藕丁.md","filePath":"guide/vegetable_dish/小炒藕丁/小炒藕丁.md","lastUpdated":1747118079000}'),s={name:"guide/vegetable_dish/小炒藕丁/小炒藕丁.md"};function d(n,l,h,o,_,c){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="小炒藕丁的做法" tabindex="-1">小炒藕丁的做法 <a class="header-anchor" href="#小炒藕丁的做法" aria-label="Permalink to “小炒藕丁的做法”">​</a></h1><p><img src="'+r+'" alt="小炒藕丁成品"></p><p>小炒藕丁是一道简单易做的菜，莲藕营养丰富，非常适合素食。预计制作时长 20 分钟</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>大葱</li><li>小米辣</li><li>莲藕</li><li>生抽</li><li>老抽</li><li>耗油</li><li>油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 2 个人吃。</p><p>每份：</p><ul><li>大葱 1 段</li><li>小米辣 1-2 根 （看个人吃辣程度）</li><li>莲藕 1 段</li><li>生抽 30 ml</li><li>老抽 15 ml</li><li>耗油 15 ml</li><li>食用油 10-15ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>大葱、小米辣切小段，备用</li><li>莲藕去皮，切成不超过 3cm 的小块，放入水中备用（防止氧化发黑）</li><li>取炒锅，锅内放入 500ml 凉水，煮沸</li><li>将藕丁下入沸水中，焯水 2 分钟后，取出放入盘中备用</li><li>将锅中水倒掉后，将锅加热干燥，加入 10-15 ml 食用油</li><li>待油温升高后，下入葱花，小米辣爆香</li><li>将处理好的藕丁下入锅中，大火翻炒</li><li>加入生抽、老抽、耗油</li><li>翻炒 2 分钟即可出锅</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>食用莲藕要挑选外皮呈黄褐色、肉肥厚而白的。如果发黑，有异味，则不宜食用。</li><li>请尽量不要使用铁器，会导致莲藕发黑</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',15)]))}const u=i(s,[["render",d]]);export{m as __pageData,u as default};
