import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E5%B9%B2%E9%94%85%E8%8A%B1%E8%8F%9C.CLbh-Kpk.jpg",p=JSON.parse('{"title":"干锅花菜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/干锅花菜/干锅花菜.md","filePath":"guide/vegetable_dish/干锅花菜/干锅花菜.md","lastUpdated":1747034379000}'),o={name:"guide/vegetable_dish/干锅花菜/干锅花菜.md"};function s(d,l,n,h,_,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="干锅花菜的做法" tabindex="-1">干锅花菜的做法 <a class="header-anchor" href="#干锅花菜的做法" aria-label="Permalink to &quot;干锅花菜的做法&quot;">​</a></h1><p><img src="'+r+'" alt="干锅花菜成品"></p><p>干锅花菜是湘菜常见的一道菜。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>花菜</li><li>五花肉</li><li>辣椒</li><li>生抽</li><li>白糖</li><li>蒜</li><li>盐</li><li>油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>花菜 400 g</li><li>五花肉 100 g</li><li>辣椒 1-2 根</li><li>生抽 10 ml</li><li>白糖 5g</li><li>蒜瓣 3-4 个</li><li>盐 2 g</li><li>油 10 ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>花菜朵朝下，没入淡盐水中浸泡 20 分钟。然后洗净用小刀拆成小朵</li><li>入开水锅中焯水 1 分钟，捞出立即用冷水冲淋至完全凉透，沥水备用</li><li>五花肉切成薄片，大蒜白色切下用刀背拍扁，小红辣椒切成段</li><li>锅烧热放油，油热下大葱白爆香</li><li>下五花肉片入锅，用中火煸炒至表面全部变色，继续煸炒一会儿，把肥肉部分的油份逼出一部分</li><li>倒入红辣椒段和花菜，翻炒几下</li><li>加入 10 ml 生抽</li><li>再加入 5 g 白糖，转大火不断翻炒 1 分钟</li><li>把大蒜叶部分切成段，放入锅中，翻炒几下后，关火盖上盖子焖 1 分钟即可</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const m=i(o,[["render",s]]);export{p as __pageData,m as default};
