import{_ as a,c as i,o as e,ah as s}from"./chunks/framework.HzbgFGQH.js";const t="/assets/1.Bus2MsDy.jpeg",r="/assets/2.D4lE1Fr_.jpeg",_="/assets/3.z1lsT6Pf.jpeg",o="/assets/4.DBQcBcJr.jpeg",f=JSON.parse('{"title":"拔丝土豆的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/拔丝土豆/拔丝土豆.md","filePath":"guide/vegetable_dish/拔丝土豆/拔丝土豆.md","lastUpdated":1747108385000}'),n={name:"guide/vegetable_dish/拔丝土豆/拔丝土豆.md"};function d(c,l,h,p,m,u){return e(),i("div",null,l[0]||(l[0]=[s('<h1 id="拔丝土豆的做法" tabindex="-1">拔丝土豆的做法 <a class="header-anchor" href="#拔丝土豆的做法" aria-label="Permalink to “拔丝土豆的做法”">​</a></h1><p>拔丝土豆是一道色香味俱全的特色名菜，属于鲁菜系</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>土豆</li><li>食用油</li><li>淀粉</li><li>白砂糖</li><li>水</li><li>芝麻</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>土豆 2 个</li><li>食用油 300 ml （土豆能浮在油上面即可）</li><li>淀粉 30 g</li><li>白砂糖 120 g （要多放糖，这是为了在土豆上面裹上一层厚厚的糖浆，从而产生拔丝的效果）</li><li>水 100 ml</li><li>芝麻 5 g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>土豆去皮，切均匀的小块。放入淀粉（不加水）搅拌，使得淀粉覆盖土豆表面</li><li>起锅烧油，放入土豆块，缓缓翻滚煎炸 5-7 分钟 ，直至筷子可以插进土豆</li><li>取出土豆，放入大碗备用</li><li>锅中加入水、白砂糖，沿着一个方向慢慢搅动白砂糖，直到白砂糖颜色变成褐色</li><li>重新倒入土豆，翻炒 30 S 后 出锅</li><li>土豆盛盘，散上芝麻</li></ul><p><img src="'+t+'" alt="示例菜成品"><img src="'+r+'" alt="示例菜成品"><img src="'+_+'" alt="示例菜成品"><img src="'+o+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>土豆煎炸不可太熟透，否则在拔丝端盘时不容易定型，影响美观</li><li>对于使用剩余的食用油，可以考虑重新收集到油壶。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const b=a(n,[["render",d]]);export{f as __pageData,b as default};
