import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"松仁玉米的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/松仁玉米.md","filePath":"guide/vegetable_dish/松仁玉米.md","lastUpdated":1747034379000}'),r={name:"guide/vegetable_dish/松仁玉米.md"};function o(d,l,s,n,h,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="松仁玉米的做法" tabindex="-1">松仁玉米的做法 <a class="header-anchor" href="#松仁玉米的做法" aria-label="Permalink to &quot;松仁玉米的做法&quot;">​</a></h1><p>松仁玉米是一道色香味俱全的家常菜，口感甜嫩清爽，松仁香脆，老少皆宜。</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>玉米粒（建议使用甜玉米）</li><li>熟松子仁</li><li>胡萝卜（可选，增加色彩）</li><li>食用油</li><li>白砂糖</li><li>盐</li><li>淀粉</li><li>水</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>玉米粒 200 g（可用罐头甜玉米，也可自备煮熟）</li><li>熟松子仁 30 g</li><li>胡萝卜 50 g（切小丁，可省略）</li><li>食用油 15 ml</li><li>白砂糖 10 g</li><li>盐 1 g</li><li>淀粉 5 g</li><li>水 20 ml（用于调淀粉水）</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>玉米粒和胡萝卜丁提前焯水 1 分钟，捞出沥干备用</li><li>热锅凉油，放入胡萝卜丁略炒，再加入玉米粒翻炒</li><li>加入白砂糖和盐，炒匀</li><li>混合水与淀粉成水淀粉，倒入锅中快速翻炒使汤汁略稠</li><li>加入熟松仁翻炒均匀</li><li>出锅装盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>松仁可以提前用小火干炒至微微金黄香脆，更加美味</li><li>使用罐头玉米时应先沥干水分，避免炒制过程中出水过多</li><li>炒制时火候不宜过大，以防糊锅或松仁焦糊</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=i(r,[["render",o]]);export{c as __pageData,p as default};
