import{_ as l,c as a,o as e,ah as s}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E6%A4%92%E7%9B%90%E7%8E%89%E7%B1%B3.H1rx2sZx.jpeg",g=JSON.parse('{"title":"椒盐玉米的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/椒盐玉米/椒盐玉米.md","filePath":"guide/vegetable_dish/椒盐玉米/椒盐玉米.md","lastUpdated":1747108385000}'),t={name:"guide/vegetable_dish/椒盐玉米/椒盐玉米.md"};function n(h,i,d,o,_,c){return e(),a("div",null,i[0]||(i[0]=[s('<h1 id="椒盐玉米的做法" tabindex="-1">椒盐玉米的做法 <a class="header-anchor" href="#椒盐玉米的做法" aria-label="Permalink to “椒盐玉米的做法”">​</a></h1><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>玉米粒</li><li>椒盐</li><li>芝麻粒</li><li>油</li><li>淀粉</li><li>两个塑料簸箕</li><li>若干吸油纸</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>这道菜分量和在川菜馆点的标准分量一样</p><ul><li>玉米粒（袋装） 350g</li><li>淀粉（在锅里完全能盖住玉米粒表面为准，在 40 - 70g）</li><li>椒盐粉 10g</li><li>芝麻粒 10g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>玉米粒都是剥好的，直接解冻即可，温水泡 15 分钟或者灶上开水煮 5 分钟。</li><li>拿出一个簸箕，将其假设为 BoxA，垫上吸油纸，倒进解冻好的玉米粒。</li><li>shaking shaking shaking! - 直到吸油纸全部变湿为止。</li><li>拿出第二个簸箕 BoxB，垫上吸油纸，将 BoxA 的玉米粒全部倒入 BoxB 中。</li><li>shaking shaking shaking! - 直到吸油纸全部变湿为止。</li><li>重复上述操作多次，直到玉米表面没有明显可见的水滴但保持湿润的状态。</li><li>倒入大量淀粉，能够完全盖住玉米粒。</li><li>shaking shaking shaking! - 直到淀粉裹住了玉米粒</li><li>开灶 - 放锅 - 倒入油 尽量铺满锅底 但不要太多。</li><li>油热 8 成，倒入裹上了淀粉的玉米粒。</li><li>中火先煎 30s，不要翻炒，不然淀粉会掉。</li><li>轻微翻炒 3 分钟即可出锅。</li><li>最重要的一步：撒上 3g 椒盐，撒上芝麻粒！</li><li>香喷喷的”椒盐玉米“就做好了</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>玉米粒不要自己剥，费手的，可以直接外卖软件买菜”已经剥好的玉米粒“，30 分钟 送达后温水解冻。 开水解冻慢就拿开水煮！（ 千万别煮玉米本身，带着包装一起煮，撕掉一个口</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p><p><img src="'+r+'" alt="椒盐玉米"></p>',13)]))}const u=l(t,[["render",n]]);export{g as __pageData,u as default};
