import{_ as i,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E6%A6%84%E8%8F%9C%E8%82%89%E6%9C%AB%E5%9B%9B%E5%AD%A3%E8%B1%86.fS5jVJnJ.JPG",m=JSON.parse('{"title":"榄菜肉末四季豆的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/榄菜肉末四季豆/榄菜肉末四季豆.md","filePath":"guide/vegetable_dish/榄菜肉末四季豆/榄菜肉末四季豆.md","lastUpdated":1747108270000}'),s={name:"guide/vegetable_dish/榄菜肉末四季豆/榄菜肉末四季豆.md"};function n(d,l,_,o,h,c){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="榄菜肉末四季豆的做法" tabindex="-1">榄菜肉末四季豆的做法 <a class="header-anchor" href="#榄菜肉末四季豆的做法" aria-label="Permalink to “榄菜肉末四季豆的做法”">​</a></h1><p><img src="'+r+'" alt="榄菜肉末四季豆成品"></p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>四季豆</li><li>五花肉</li><li>橄榄菜</li><li>大蒜</li><li>小米辣（不吃辣可以不放）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><ul><li>四季豆 220g</li><li>五花肉 100g</li><li>橄榄菜 20g</li><li>大蒜 10g</li><li>小米辣 10g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>将四季豆洗净，并把筋撕干净，然后切成大小均匀的颗粒备用。</li><li>将大蒜拍碎剁成蒜末备用。</li><li>将小米辣切成大小均匀的颗粒备用。</li><li>将五花肉去皮，然后剁成肉末备用。</li><li>将锅烧热，然后加入 20ml 油滑锅，锅滑好之后将热油倒出，然后加入 10ml 冷油，这就是传说中热锅冷油，这么做主要是防止肉末粘锅。</li><li>如果家里没有晾油瓶的话，也可以不用滑锅，放入油之后，直接加入肉末开始煸炒，小火煸炒两分钟，炒出猪油。</li><li>肉末炒香之后加入蒜末，橄榄菜和小米辣，炒出香味。</li><li>加入四季豆开中火煸炒，四季豆至少要炒 5 分钟，一定要保证四季豆<strong>熟透</strong>，否则可能会食物中毒。</li><li>四季豆炒熟后加入 2ml 酱油从锅边淋入，然后加入 2g 盐、1g 鸡精、1g 胡椒粉和 0.5g 糖。</li><li>将调料翻炒均匀。</li><li>出锅，装盘。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',11)]))}const p=i(s,[["render",n]]);export{m as __pageData,p as default};
