import{_ as l,c as e,o as i,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"水油焖蔬菜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/水油焖蔬菜.md","filePath":"guide/vegetable_dish/水油焖蔬菜.md","lastUpdated":1747034379000}'),r={name:"guide/vegetable_dish/水油焖蔬菜.md"};function o(d,a,s,n,h,u){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="水油焖蔬菜的做法" tabindex="-1">水油焖蔬菜的做法 <a class="header-anchor" href="#水油焖蔬菜的做法" aria-label="Permalink to &quot;水油焖蔬菜的做法&quot;">​</a></h1><p>水油焖蔬菜中添加了油，这提升了口感，并且可提升脂溶性维生素的摄入。相比生吃蔬菜，好处更多。</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>食用油</li><li>盐</li><li>蚝油（可选）</li><li>叶菜类蔬菜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>叶菜类蔬菜含水量较多，在准备时可能看起来体积很大。实际受热后体积会迅速减小。</p><ul><li>叶菜类蔬菜：300g ~ 500g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>洗净蔬菜</li><li>锅中加入 150ml 水，并烧开。（水不需要能完全没过蔬菜）</li><li>加入 3g 盐</li><li>（可选）加入 3ml 蚝油</li><li>加入 2ml 食用油</li><li>下菜， 翻拌一下，然后盖上锅盖焖 1 分钟</li><li>盛盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>不确定咸淡的情况， 可以先少放盐， 在出锅前尝味， 考虑调整加盐</li><li>可以用鸡汤、骨头汤等替代水， 更好吃</li><li>焖的时间不可过长</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=l(r,[["render",o]]);export{c as __pageData,p as default};
