import{_ as a,c as l,o as i,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E7%82%92%E6%BB%91%E8%9B%8B.D_-yk8Fk.jpg",m=JSON.parse('{"title":"炒滑蛋的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/炒滑蛋/炒滑蛋.md","filePath":"guide/vegetable_dish/炒滑蛋/炒滑蛋.md","lastUpdated":1747108270000}'),s={name:"guide/vegetable_dish/炒滑蛋/炒滑蛋.md"};function d(h,e,n,o,_,c){return i(),l("div",null,e[0]||(e[0]=[r('<h1 id="炒滑蛋的做法" tabindex="-1">炒滑蛋的做法 <a class="header-anchor" href="#炒滑蛋的做法" aria-label="Permalink to “炒滑蛋的做法”">​</a></h1><p><img src="'+t+'" alt="炒滑蛋成品"></p><p>炒滑蛋是一道简单易做的菜。一般初学者只需要 5 分钟即可完成。</p><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>鸡蛋（最好是无菌蛋）</li><li>牛奶</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>鸡蛋 4 颗</li><li>牛奶 30ml</li><li>食用油 10ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>鸡蛋加入牛奶以及 5ml 食用油搅拌均匀，备用</li><li>大火烧热平底锅约 30s, 加入 5ml 食用油</li><li>烧 30s 转小火, 并且放入搅拌好的鸡蛋</li><li>在锅中静置 5 秒后，用锅铲将蛋液从边缘缓慢推向中间</li><li>翻炒至鸡蛋大致凝固后关火，装盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>最后可根据个人口味撒些黑胡椒或者盐来调味。</li></ul><p>参考资料</p><ul><li><a href="http://sweetheartkitchen.com/recipes/scrambled-egg/" target="_blank" rel="noreferrer">甜琛廚房</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',16)]))}const u=a(s,[["render",d]]);export{m as __pageData,u as default};
