import{_ as e,c as l,o as i,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E7%81%AB%E5%B1%B1%E9%A3%98%E9%9B%AA.CE0zeqjD.jpg",u=JSON.parse('{"title":"糖拌西红柿的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/糖拌西红柿/糖拌西红柿.md","filePath":"guide/vegetable_dish/糖拌西红柿/糖拌西红柿.md","lastUpdated":1747118079000}'),s={name:"guide/vegetable_dish/糖拌西红柿/糖拌西红柿.md"};function d(n,a,h,_,o,p){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="糖拌西红柿的做法" tabindex="-1">糖拌西红柿的做法 <a class="header-anchor" href="#糖拌西红柿的做法" aria-label="Permalink to “糖拌西红柿的做法”">​</a></h1><p><img src="'+r+'" alt="示例菜成品"></p><p>新鲜可口，制作简便，营养价值高，适合夏季食用，家庭餐桌上的一道美味凉菜。西红柿含有大量的维生素 C, 做法简单 几分钟就可完成。</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>西红柿</li><li>白砂糖</li><li>冰箱</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>一份正好够 2 人吃。</p><p>每份：</p><ul><li>西红柿 2 个（每个西红柿约 100g，共 200g）</li><li>白砂糖 20g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>用刀将西红柿皮米字型划开</li><li>用筷子插入西红柿的菊花，在燃气上转动烤 10 秒（或用开水冲 30 秒），直到西红柿皮卷边</li><li>把西红柿的衣服脱光</li><li>再西红柿大卸八块（沿纹路切可以更多的留汁水），去掉头部根蒂部，备用</li><li>全部切好后，将西红柿在盘子中均匀码一层</li><li>撒上白糖，重复上面一步直到全部西红柿放完</li><li>放入冰箱冷藏 10 分钟</li><li>一盘糖拌西红柿就好了，营养美味，酸甜爽口，夏日解暑又解腻</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>在制作过程中 请您小心使用刀具。</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',15)]))}const m=e(s,[["render",d]]);export{u as __pageData,m as default};
