import{_ as i,c as e,o as a,ah as r}from"./chunks/framework.HzbgFGQH.js";const c=JSON.parse('{"title":"素炒豆角的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/素炒豆角.md","filePath":"guide/vegetable_dish/素炒豆角.md","lastUpdated":1747108385000}'),t={name:"guide/vegetable_dish/素炒豆角.md"};function s(d,l,h,n,o,_){return a(),e("div",null,l[0]||(l[0]=[r('<h1 id="素炒豆角的做法" tabindex="-1">素炒豆角的做法 <a class="header-anchor" href="#素炒豆角的做法" aria-label="Permalink to “素炒豆角的做法”">​</a></h1><p>巨下饭的家常菜</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>豆角</li><li>小米椒</li><li>葱</li><li>蒜</li><li>生抽</li><li>老抽</li><li>耗油</li><li>盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>注意，受食材个体影响，本菜谱为 2 人份菜谱</p><ul><li>豆角 250g</li><li>小米椒 2 个</li><li>葱 3 圈</li><li>蒜 2 颗</li><li>生抽 6ml</li><li>老抽 2ml</li><li>耗油 6ml</li><li>盐 6g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>葱切花，蒜切沫，备用。</li><li>生抽、老抽、耗油、盐混合调料汁，备用。</li><li>小米椒切圈，备用。</li><li>豆角去筋，45° 斜切<em>4-10cm</em>小段，备用。</li><li>起锅烧油(10ml - 15ml)，冒烟后放入葱、小米椒，翻炒至闻到香味；</li><li>加入豆角，翻炒<em>30s</em>,</li><li>加入料汁，开大火翻炒<em>2分钟</em></li><li>倒入 150ml 水</li><li>转中小火，盖上锅盖焖制 8-10 分钟</li><li>加入蒜切沫，出锅。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>切豆角需要一定的刀工，不会的可以使用剪刀</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=i(t,[["render",s]]);export{c as __pageData,p as default};
