import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/1.Bull6wYQ.jpeg",s="/assets/2.DHUaFLN3.jpeg",m=JSON.parse('{"title":"红烧冬瓜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/红烧冬瓜/红烧冬瓜.md","filePath":"guide/vegetable_dish/红烧冬瓜/红烧冬瓜.md","lastUpdated":1747107234000}'),o={name:"guide/vegetable_dish/红烧冬瓜/红烧冬瓜.md"};function d(n,l,_,h,c,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="红烧冬瓜的做法" tabindex="-1">红烧冬瓜的做法 <a class="header-anchor" href="#红烧冬瓜的做法" aria-label="Permalink to &quot;红烧冬瓜的做法&quot;">​</a></h1><p>红烧冬瓜是一道具有色泽红亮，香鲜味美、营养价值丰富的家常菜</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>冬瓜</li><li>食用油</li><li>料酒</li><li>淀粉</li><li>生抽</li><li>老抽</li><li>鸡精</li><li>香葱</li><li>姜末</li><li>蚝油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>冬瓜 300 g</li><li>食用油 50 ml</li><li>料酒 2 ml</li><li>淀粉 10 g</li><li>生抽 10 ml</li><li>老抽 15 ml</li><li>鸡精 3 g</li><li>香葱 0.5 根</li><li>姜末 1 粒</li><li>蚝油 15 ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>冬瓜去皮，切 边长不超过 2cm 小块</li><li>起锅烧油，放入冬瓜，缓缓翻滚煎炸 2 分钟 ，直至冬瓜表面泛金黄色</li><li>取出冬瓜，放入大碗备用</li><li>利用锅中的剩余油，依次放入姜末、生抽、蚝油，翻炒 15 S</li><li>重新倒入冬瓜，翻炒 30 S 后，加入开水，水要没过冬瓜表面，大火煮 10 分钟</li><li>加入老抽上色，继续煮，直至冬瓜软糯（筷子可以轻松插近冬瓜）</li><li>加入鸡精、料酒、香葱翻炒后 30 S， 取出冬瓜到大碗中</li><li>锅中剩余汤汁保留，倒入水淀粉，煮开后汤汁浇灌在冬瓜表面</li></ul><p><img src="'+r+'" alt="示例菜成品"><img src="'+s+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const g=i(o,[["render",d]]);export{m as __pageData,g as default};
